import React, { useEffect, useState, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { jwtDecode } from "jwt-decode";
import "./Chat.css";

const Chat = () => {
    const [contacts, setContacts] = useState([]);
    const [messages, setMessages] = useState([]);
    const [activeContact, setActiveContact] = useState(null);
    const [text, setText] = useState("");
    const [myId, setMyId] = useState(null);
    const [connected, setConnected] = useState(false);
    const clientRef = useRef(null);

    const getToken = () => {
        const match = document.cookie.match(new RegExp("(^| )token=([^;]+)"));
        return match ? match[2] : null;
    };

    useEffect(() => {
        const token = getToken();
        if (!token) {
            alert("‚õî –ù–µ—Ç —Ç–æ–∫–µ–Ω–∞. –ê–≤—Ç–æ—Ä–∏–∑—É–π—Ç–µ—Å—å —Å–Ω–æ–≤–∞.");
            window.location.href = "/";
            return;
        }

        const decoded = jwtDecode(token);
        const userId = decoded.sub || decoded.userId;
        setMyId(userId);

        connectToWebSocket(token, userId);
        loadContacts(userId);
    }, []);

    const connectToWebSocket = (token, userId) => {
        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            connectHeaders: {
                Authorization: `Bearer ${token}`,
            },
            onConnect: () => {
                setConnected(true);
                client.subscribe(`/user/queue/messages`, (msg) => {
                    const body = JSON.parse(msg.body);
                    if (
                        body.senderId === activeContact?.id ||
                        body.recipientId === activeContact?.id
                    ) {
                        setMessages((prev) => [...prev, body]);
                    } else {
                        alert(`–ù–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ –æ—Ç ${body.senderName}`);
                    }
                });
            },
            onStompError: (frame) => {
                console.error("STOMP –æ—à–∏–±–∫–∞:", frame);
            },
            reconnectDelay: 5000,
        });

        client.activate();
        clientRef.current = client;
    };

    const loadContacts = async (userId) => {
        try {
            const res = await fetch(`/users`, {
                headers: { Authorization: `Bearer ${getToken()}` },
            });
            const data = await res.json();
            setContacts(data.filter((u) => u.id !== userId));
            setActiveContact(data[0]);
        } catch (err) {
            console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∫–æ–Ω—Ç–∞–∫—Ç–æ–≤", err);
        }
    };

    const loadMessages = async (recipientId) => {
        try {
            const res = await fetch(`/messages/${myId}/${recipientId}`);
            const data = await res.json();
            setMessages(data);
        } catch (err) {
            console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Å–æ–æ–±—â–µ–Ω–∏–π", err);
        }
    };

    useEffect(() => {
        if (myId && activeContact?.id) {
            loadMessages(activeContact.id);
        }
    }, [activeContact]);

    const sendMessage = () => {
        if (!text.trim()) return;
        const payload = {
            senderId: myId,
            recipientId: activeContact.id,
            content: text,
        };

        clientRef.current.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages((prev) => [...prev, payload]);
        setText("");
    };

    return (
        <div className="chat-wrapper">
            <div className="sidebar">
                <h3>–ö–æ–Ω—Ç–∞–∫—Ç—ã</h3>
                {contacts.map((c) => (
                    <div
                        key={c.id}
                        className={`contact ${activeContact?.id === c.id ? "active" : ""}`}
                        onClick={() => setActiveContact(c)}
                    >
                        {c.name}
                    </div>
                ))}
            </div>
            <div className="chat-panel">
                <div className="chat-header">
                    <h3>{activeContact?.name}</h3>
                    <span>{connected ? "üü¢ –ü–æ–¥–∫–ª—é—á–µ–Ω–æ" : "üî¥ –ù–µ—Ç –ø–æ–¥–∫–ª—é—á–µ–Ω–∏—è"}</span>
                </div>
                <div className="chat-messages">
                    {messages.map((m, idx) => (
                        <div key={idx} className={m.senderId === myId ? "mine" : "theirs"}>
                            <b>{m.senderId === myId ? "–Ø" : activeContact.name}</b>:{" "}
                            {m.content}
                        </div>
                    ))}
                </div>
                <div className="chat-input">
                    <input
                        type="text"
                        value={text}
                        onChange={(e) => setText(e.target.value)}
                        onKeyDown={(e) => {
                            if (e.key === "Enter") sendMessage();
                        }}
                        placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ..."
                    />
                    <button onClick={sendMessage}>–û—Ç–ø—Ä–∞–≤–∏—Ç—å</button>
                </div>
            </div>
        </div>
    );
};

export default Chat;
