import React, { useEffect, useState, useRef } from "react";
import { Client } from "@stomp/stompjs";
import { jwtDecode } from "jwt-decode";
import "./Chat.css"; // —Å—Ç–∏–ª—å –æ—Ñ–æ—Ä–º–ª–µ–Ω–Ω—è

function Chat() {
    const [myId, setMyId] = useState(null);
    const [myName, setMyName] = useState("");
    const [recipientId, setRecipientId] = useState("");
    const [allUsers, setAllUsers] = useState([]);
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState("");
    const [connected, setConnected] = useState(false);

    const clientRef = useRef(null);
    const recipientIdRef = useRef("");

    const getToken = () => {
        const match = document.cookie.match(new RegExp("(^| )token=([^;]+)"));
        return match ? match[2] : null;
    };

    useEffect(() => {
        const token = getToken();
        if (!token) {
            alert("–¢–æ–∫–µ–Ω –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç. –ü–µ—Ä–µ–∑–∞–π–¥–∏—Ç–µ.");
            window.location.href = "/";
            return;
        }

        const decoded = jwtDecode(token);
        const userId = decoded.userId;
        const username = decoded.username;

        setMyId(userId);
        setMyName(username);

        const client = new Client({
            brokerURL: "ws://localhost:8080/ws", // WebSocket endpoint –±–µ–∑ SockJS
            connectHeaders: {
                Authorization: `Bearer ${token}`,
            },
            reconnectDelay: 3000,
            onConnect: () => {
                setConnected(true);
                console.log("‚úÖ WebSocket –ø–æ–¥–∫–ª—é—á–µ–Ω");

                client.subscribe(`/user/${userId}/queue/messages`, (message) => {
                    const msg = JSON.parse(message.body);
                    if (recipientIdRef.current === msg.senderId) {
                        setMessages((prev) => [...prev, {
                            sender: msg.senderName,
                            text: msg.content,
                            timestamp: msg.timestamp,
                        }]);
                    } else {
                        console.log("üì® –ù–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ –æ—Ç:", msg.senderName);
                    }
                });
            },
            onStompError: (frame) => {
                console.error("STOMP –æ—à–∏–±–∫–∞:", frame);
            }
        });

        client.activate();
        clientRef.current = client;

        return () => {
            client.deactivate();
        };
    }, []);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const token = getToken();
                const res = await fetch("/users/all", {
                    headers: { Authorization: `Bearer ${token}` },
                });
                const users = await res.json();
                setAllUsers(users.filter((u) => u.id !== myId));
            } catch (err) {
                console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π:", err);
            }
        };

        if (myId) fetchUsers();
    }, [myId]);

    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const token = getToken();
                const res = await fetch(`/messages/${myId}/${recipientId}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                const data = await res.json();
                setMessages(data.map((msg) => ({
                    sender: msg.senderName,
                    text: msg.content,
                    timestamp: msg.timestamp,
                })));
            } catch (err) {
                console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Å–æ–æ–±—â–µ–Ω–∏–π:", err);
            }
        };

        if (recipientId && myId) {
            fetchMessages();
        }
    }, [recipientId, myId]);

    const handleSend = (e) => {
        e.preventDefault();
        const client = clientRef.current;
        if (!message.trim() || !recipientId || !client || !client.connected) return;

        const recipientUser = allUsers.find((u) => u.id === recipientId);

        const payload = {
            senderId: myId,
            recipientId,
            senderName: myName,
            recipientName: recipientUser?.username || "–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å",
            content: message,
            timestamp: new Date().toISOString(),
        };

        client.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages((prev) => [...prev, {
            sender: "–Ø",
            text: message,
            timestamp: new Date().toLocaleString(),
        }]);
        setMessage("");
    };

    const handleRecipientChange = (e) => {
        const value = e.target.value;
        recipientIdRef.current = value;
        setRecipientId(value);
    };

    return (
        <div className="chat-container">
            <h2>üí¨ –ß–∞—Ç</h2>
            <div>Status: {connected ? "üü¢ –ü–æ–¥–∫–ª—é—á–µ–Ω–æ" : "üî¥ –û—Ç–∫–ª—é—á–µ–Ω–æ"}</div>
            <select onChange={handleRecipientChange} value={recipientId}>
                <option value="">-- –í—ã–±–µ—Ä–∏—Ç–µ –ø–æ–ª—É—á–∞—Ç–µ–ª—è --</option>
                {allUsers.map((user) => (
                    <option key={user.id} value={user.id}>
                        {user.username} ({user.email})
                    </option>
                ))}
            </select>

            <div className="messages">
                {messages.map((msg, idx) => (
                    <div key={idx}>
                        <strong>{msg.sender}:</strong> {msg.text}
                        <div className="timestamp">{msg.timestamp}</div>
                    </div>
                ))}
            </div>

            <form onSubmit={handleSend}>
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ..."
                />
                <button type="submit">–û—Ç–ø—Ä–∞–≤–∏—Ç—å</button>
            </form>
        </div>
    );
}

export default Chat;
