import React, { useState, useEffect, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { jwtDecode } from "jwt-decode";
import "./Chat.css";

function Chat() {
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);
    const [recipientId, setRecipientId] = useState("");
    const [myId, setMyId] = useState(null);
    const [connected, setConnected] = useState(false);
    const [dialogs, setDialogs] = useState([]);
    const clientRef = useRef(null);

    const getToken = () => {
        const match = document.cookie.match(new RegExp("(^| )token=([^;]+)"));
        return match ? match[2] : null;
    };

    const fetchMessages = async (recipientId) => {
        try {
            const token = getToken();
            const res = await fetch(`/messages/${myId}/${recipientId}`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            if (res.ok) {
                const data = await res.json();
                setMessages(
                    data.map((msg) => ({
                        sender: msg.senderId === myId ? "–Ø" : "–û–Ω",
                        text: msg.content,
                    }))
                );
            } else {
                console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Å–æ–æ–±—â–µ–Ω–∏–π:", res.status);
            }
        } catch (err) {
            console.error("–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å —Å–æ–æ–±—â–µ–Ω–∏—è", err);
        }
    };

    useEffect(() => {
        const token = getToken();
        if (!token) {
            alert("‚ùå –ù–µ—Ç —Ç–æ–∫–µ–Ω–∞. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–æ–π–¥–∏—Ç–µ —Å–Ω–æ–≤–∞.");
            window.location.href = "/";
            return;
        }

        const decoded = jwtDecode(token);
        const id = decoded.userId;
        if (!id) {
            alert("‚ö†Ô∏è –¢–æ–∫–µ–Ω –Ω–µ —Å–æ–¥–µ—Ä–∂–∏—Ç userId. –ù—É–∂–Ω–∞ –∫–æ—Ä—Ä–µ–∫—Ç–∏—Ä–æ–≤–∫–∞ –±—ç–∫–∞.");
            return;
        }
        setMyId(id);

        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            connectHeaders: { Authorization: `Bearer ${token}` },
            onConnect: () => {
                console.log("üü¢ –ü–æ–¥–∫–ª—é—á–µ–Ω–æ –∫ WebSocket");
                setConnected(true);

                client.subscribe("/user/queue/messages", (msg) => {
                    const body = JSON.parse(msg.body);
                    const isForCurrentChat =
                        (body.senderId === recipientId && body.recipientId === myId) ||
                        (body.senderId === myId && body.recipientId === recipientId);

                    if (isForCurrentChat) {
                        setMessages((prev) => [
                            ...prev,
                            {
                                sender: body.senderId === myId ? "–Ø" : "–û–Ω",
                                text: body.content,
                            },
                        ]);
                    }
                });
            },
            onStompError: (frame) => {
                console.error("STOMP –æ—à–∏–±–∫–∞: ", frame);
            },
            reconnectDelay: 5000,
        });

        client.activate();
        clientRef.current = client;

        return () => {
            client.deactivate();
            setConnected(false);
        };
    }, []);

    // –ó–∞–≥—Ä—É–∂–∞–µ–º —Å–ø–∏—Å–æ–∫ —Å–æ–±–µ—Å–µ–¥–Ω–∏–∫–æ–≤
    useEffect(() => {
        if (!myId) return;
        const token = getToken();
        fetch(`/dialogs/${myId}`, {
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
            .then(res => res.json())
            .then(setDialogs)
            .catch(console.error);
    }, [myId]);

    const handleSend = (e) => {
        e.preventDefault();
        if (!message.trim() || !recipientId.trim()) return;

        const client = clientRef.current;

        if (!client || !client.connected) {
            alert("‚õî –ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –µ—â—ë –Ω–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ. –ü–æ–¥–æ–∂–¥–∏—Ç–µ –Ω–µ–º–Ω–æ–≥–æ.");
            return;
        }

        const payload = {
            senderId: myId,
            recipientId,
            content: message,
        };

        client.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages((prev) => [...prev, { sender: "–Ø", text: message }]);
        setMessage("");
    };

    const handleSelectDialog = (id) => {
        setRecipientId(id);
        fetchMessages(id);
    };

    return (
        <div className="chat-layout">
            <div className="sidebar">
                <h3>üí¨ –î–∏–∞–ª–æ–≥–∏</h3>
                {dialogs.length === 0 && <p>–ù–µ—Ç –ø–µ—Ä–µ–ø–∏—Å–æ–∫</p>}
                {dialogs.map((dialog) => (
                    <div
                        key={dialog.id}
                        className={`dialog-item ${recipientId === dialog.id ? "active" : ""}`}
                        onClick={() => handleSelectDialog(dialog.id)}
                    >
                        {dialog.name || dialog.id}
                    </div>
                ))}
            </div>

            <div className="chat-container">
                <div className="chat-box">
                    <div className="chat-header">
                        üí¨ –ú–µ—Å—Å–µ–Ω–¥–∂–µ—Ä
                        <span className="status">
                            {connected ? "üü¢ –ü–æ–¥–∫–ª—é—á–µ–Ω–æ" : "üî¥ –ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ..."}
                        </span>
                    </div>

                    <div className="messages">
                        {messages.map((msg, idx) => (
                            <div key={idx} className="message">
                                <strong>{msg.sender}:</strong> {msg.text}
                            </div>
                        ))}
                    </div>

                    <form onSubmit={handleSend} className="input-form">
                        <input
                            type="text"
                            value={message}
                            onChange={(e) => setMessage(e.target.value)}
                            placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ..."
                            className="message-input"
                            required
                        />
                        <button type="submit" className="send-button">
                            –û—Ç–ø—Ä–∞–≤–∏—Ç—å
                        </button>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default Chat;
