import React, { useState, useEffect, useRef } from "react";
import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs";

const ChatComponent = ({ myId }) => {
    const [client, setClient] = useState(null);
    const [connected, setConnected] = useState(false);
    const [recipientId, setRecipientId] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState("");

    const recipientIdRef = useRef(null);
    const clientRef = useRef(null);

    useEffect(() => {
        const sock = new SockJS("http://localhost:8080/ws");
        const stompClient = new Client({
            webSocketFactory: () => sock,
            onConnect: () => {
                setConnected(true);
                console.log("‚úÖ Connected");

                stompClient.subscribe(`/user/${myId}/queue/messages`, (msg) => {
                    const message = JSON.parse(msg.body);
                    const sender = message.senderId;

                    // –ï—Å–ª–∏ –æ—Ç–∫—Ä—ã—Ç –Ω—É–∂–Ω—ã–π —á–∞—Ç ‚Äî –æ–±–Ω–æ–≤–ª—è–µ–º
                    if (recipientIdRef.current === sender) {
                        setMessages((prev) => [...prev, message]);
                    } else {
                        console.log("üì¨ –ù–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ –æ—Ç:", sender);
                    }
                });
            },
            onDisconnect: () => {
                setConnected(false);
                console.log("‚ùå Disconnected");
            },
        });

        stompClient.activate();
        clientRef.current = stompClient;
        setClient(stompClient);

        return () => stompClient.deactivate();
    }, [myId]);

    const fetchMessages = async (recipient) => {
        const res = await fetch(`/api/messages?senderId=${myId}&recipientId=${recipient}`);
        const data = await res.json();
        setMessages(data);
    };

    const openChat = (id) => {
        setRecipientId(id);
        recipientIdRef.current = id;
        fetchMessages(id);
    };

    const sendMessage = () => {
        if (client && connected && newMessage && recipientId) {
            const msg = {
                senderId: myId,
                recipientId,
                content: newMessage,
            };
            client.publish({ destination: "/app/chat", body: JSON.stringify(msg) });
            setMessages((prev) => [...prev, msg]);
            setNewMessage("");
        }
    };

    return (
        <div>
            <h2>–ú–æ–π ID: {myId}</h2>
            <div>
                <button onClick={() => openChat("user2")}>–ß–∞—Ç —Å user2</button>
                <button onClick={() => openChat("user3")}>–ß–∞—Ç —Å user3</button>
            </div>
            <div>
                <h3>–°–æ–æ–±—â–µ–Ω–∏—è</h3>
                <div style={{ border: "1px solid gray", padding: "10px", height: "200px", overflowY: "scroll" }}>
                    {messages.map((msg, index) => (
                        <div key={index}>
                            <b>{msg.senderId === myId ? "–í—ã" : msg.senderId}:</b> {msg.content}
                        </div>
                    ))}
                </div>
                <input
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ"
                />
                <button onClick={sendMessage}>–û—Ç–ø—Ä–∞–≤–∏—Ç—å</button>
            </div>
        </div>
    );
};

export default ChatComponent;
