import React, { useEffect, useState, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import "./Chat.css";

function Chat() {
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);

    const userId = 1;        // ID —Ç–µ–∫—É—â–µ–≥–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
    const recipientId = 2;   // ID —Å–æ–±–µ—Å–µ–¥–Ω–∏–∫–∞

    const stompClientRef = useRef(null);

    useEffect(() => {
        // –î–æ–±–∞–≤–ª—è–µ–º userId –∫–∞–∫ query-–ø–∞—Ä–∞–º–µ—Ç—Ä
        const socket = new SockJS(`http://localhost:8080/ws?userId=${userId}`);

        const stompClient = new Client({
            webSocketFactory: () => socket,
            reconnectDelay: 5000,
            onConnect: () => {
                console.log("‚úÖ Connected to WebSocket");

                // –ü–æ–¥–ø–∏—Å–∫–∞ –±–µ–∑ userId ‚Äî —Ä–∞–±–æ—Ç–∞–µ—Ç —Å convertAndSendToUser
                stompClient.subscribe("/user/queue/messages", (msg) => {
                    const notification = JSON.parse(msg.body);
                    fetch(`http://localhost:8080/messages/${notification.id}`)
                        .then((res) => res.json())
                        .then((data) => {
                            setMessages((prev) => [
                                ...prev,
                                { sender: data.senderName, text: data.content },
                            ]);
                        });
                });
            },
            onStompError: (frame) => {
                console.error("‚ùå STOMP error", frame);
            },
        });

        stompClient.activate();
        stompClientRef.current = stompClient;

        return () => {
            stompClient.deactivate();
        };
    }, [userId]);

    const handleSend = (e) => {
        e.preventDefault();
        if (!message.trim()) return;

        const chatMessage = {
            senderId: userId,
            recipientId: recipientId,
            senderName: userId,     // –º–æ–∂–Ω–æ –∑–∞–º–µ–Ω–∏—Ç—å –Ω–∞ –∏–º—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
            recipientName: recipientId,
            content: message,
            timestamp: new Date(),
        };

        stompClientRef.current.publish({
            destination: "/app/chat",
            body: JSON.stringify(chatMessage),
        });

        setMessages((prev) => [...prev, { sender: "–Ø", text: message }]);
        setMessage("");
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                <div className="chat-header">üí¨ –ú–µ—Å—Å–µ–Ω–¥–∂–µ—Ä</div>
                <div className="messages">
                    {messages.map((msg, idx) => (
                        <div key={idx} className="message">
                            <strong>{msg.sender}:</strong> {msg.text}
                        </div>
                    ))}
                </div>
                <form onSubmit={handleSend} className="input-form">
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ..."
                        className="message-input"
                        required
                    />
                    <button type="submit" className="send-button">–û—Ç–ø—Ä–∞–≤–∏—Ç—å</button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
