import React, { useState, useEffect, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { jwtDecode } from "jwt-decode";
import "./Chat.css";

function Chat() {
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);
    const [recipientId, setRecipientId] = useState("");
    const [myId, setMyId] = useState(null);
    const [myName, setMyName] = useState("");
    const [connected, setConnected] = useState(false);
    const [users, setUsers] = useState([]); // Список пользователей

    const clientRef = useRef(null);
    const recipientIdRef = useRef("");

    const getToken = () => {
        const match = document.cookie.match(new RegExp("(^| )token=([^;]+)"));
        return match ? match[2] : null;
    };

    const fetchMessages = async (recipientId) => {
        try {
            const token = getToken();
            const res = await fetch(`/messages/${myId}/${recipientId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            if (res.ok) {
                const data = await res.json();
                setMessages(
                    data.map((msg) => ({
                        sender: msg.senderId === myId ? "Я" : "Он",
                        text: msg.content,
                        timestamp: msg.timestamp,
                        senderName: msg.senderName,
                    }))
                );
            } else {
                console.error("Ошибка загрузки сообщений:", res.status);
            }
        } catch (err) {
            console.error("Не удалось загрузить сообщения:", err);
        }
    };

    useEffect(() => {
        const saved = localStorage.getItem("lastRecipientId");
        if (saved) {
            setRecipientId(saved);
            recipientIdRef.current = saved;
        }
    }, []);

    useEffect(() => {
        if (recipientId && myId) {
            fetchMessages(recipientId);
            localStorage.setItem("lastRecipientId", recipientId);
        }
    }, [recipientId, myId]);

    useEffect(() => {
        const token = getToken();
        if (!token) {
            alert("Нет токена. Войдите снова.");
            window.location.href = "/";
            return;
        }

        const decoded = jwtDecode(token);
        const id = decoded.userId;
        const name = decoded.username;
        setMyId(id);
        setMyName(name);

        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            connectHeaders: { Authorization: `Bearer ${token}` },
            reconnectDelay: 5000,
            onConnect: () => {
                setConnected(true);
                client.subscribe("/user/queue/messages", (msg) => {
                    const body = JSON.parse(msg.body);
                    const otherUser = body.senderId === id ? body.recipientId : body.senderId;

                    // Добавляем нового пользователя в список, если его ещё нет
                    setUsers((prev) =>
                        prev.includes(otherUser) ? prev : [...prev, otherUser]
                    );

                    if (!recipientIdRef.current) {
                        recipientIdRef.current = otherUser;
                        setRecipientId(otherUser);
                    }

                    fetchMessages(recipientIdRef.current);
                });
            },
            onStompError: (frame) => {
                console.error("STOMP ошибка: ", frame);
            },
        });

        client.activate();
        clientRef.current = client;

        return () => {
            client.deactivate();
            setConnected(false);
        };
    }, []);

    const handleSend = (e) => {
        e.preventDefault();
        const client = clientRef.current;
        if (!message.trim() || !recipientId.trim()) return;

        if (!client || !client.connected) {
            alert("Подключение не установлено.");
            return;
        }

        const payload = {
            senderId: myId,
            recipientId,
            content: message,
            senderName: myName,
            recipientName: "Имя_получателя", // по желанию
        };

        client.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages((prev) => [
            ...prev,
            { sender: "Я", text: message, timestamp: new Date().toLocaleString(), senderName: myName },
        ]);
        setMessage("");

        if (!users.includes(recipientId)) {
            setUsers((prev) => [...prev, recipientId]);
        }
    };

    const handleRecipientSelect = (e) => {
        const value = e.target.value;
        setRecipientId(value);
        recipientIdRef.current = value;
        localStorage.setItem("lastRecipientId", value);
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                <div className="chat-header">💬 Мессенджер</div>
                <div className="status">{connected ? "🟢 Подключено" : "🔴 Подключение..."}</div>

                <select
                    value={recipientId}
                    onChange={handleRecipientSelect}
                    className="recipient-select"
                >
                    <option value="" disabled>Выберите получателя</option>
                    {users.map((uid) => (
                        <option key={uid} value={uid}>{uid}</option>
                    ))}
                </select>

                <div className="messages">
                    {messages.map((msg, idx) => (
                        <div key={idx} className="message">
                            <strong>{msg.senderName}:</strong> {msg.text} <span>{msg.timestamp}</span>
                        </div>
                    ))}
                </div>

                <form onSubmit={handleSend} className="input-form">
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Введите сообщение..."
                        className="message-input"
                        required
                    />
                    <button type="submit" className="send-button">
                        Отправить
                    </button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
