import React, { useState, useEffect, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { jwtDecode } from "jwt-decode";
import "./Chat.css";

function Chat() {
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);
    const [recipientId, setRecipientId] = useState("");
    const [myId, setMyId] = useState(null);
    const [myName, setMyName] = useState("");
    const [connected, setConnected] = useState(false);

    const clientRef = useRef(null);
    const recipientIdRef = useRef("");

    const getToken = () => {
        const match = document.cookie.match(new RegExp("(^| )token=([^;]+)"));
        return match ? match[2] : null;
    };

    const fetchMessages = async (recipientId) => {
        try {
            const token = getToken();
            const res = await fetch(`/messages/${myId}/${recipientId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            if (res.ok) {
                const data = await res.json();
                setMessages(
                    data.map((msg) => ({
                        sender: msg.senderId === myId ? "–Ø" : "–û–Ω",
                        text: msg.content,
                        timestamp: msg.timestamp,
                        senderName: msg.senderName,
                    }))
                );
            } else {
                console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Å–æ–æ–±—â–µ–Ω–∏–π:", res.status);
            }
        } catch (err) {
            console.error("–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å —Å–æ–æ–±—â–µ–Ω–∏—è:", err);
        }
    };

    // –ü–æ–¥–≥—Ä—É–∂–∞–µ–º —Å–æ—Ö—Ä–∞–Ω—ë–Ω–Ω—ã–π recipientId –∏–∑ localStorage
    useEffect(() => {
        const saved = localStorage.getItem("lastRecipientId");
        if (saved) {
            setRecipientId(saved);
            recipientIdRef.current = saved;
        }
    }, []);

    // –ó–∞–≥—Ä—É–∂–∞–µ–º –∏—Å—Ç–æ—Ä–∏—é –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏ –ø–æ–ª—É—á–∞—Ç–µ–ª—è
    useEffect(() => {
        if (recipientId && myId) {
            fetchMessages(recipientId);
            localStorage.setItem("lastRecipientId", recipientId);
        }
    }, [recipientId, myId]);

    // WebSocket –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è
    useEffect(() => {
        const token = getToken();
        if (!token) {
            alert("–ù–µ—Ç —Ç–æ–∫–µ–Ω–∞. –í–æ–π–¥–∏—Ç–µ —Å–Ω–æ–≤–∞.");
            window.location.href = "/";
            return;
        }

        const decoded = jwtDecode(token);
        const id = decoded.userId;
        const name = decoded.username;
        setMyId(id);
        setMyName(name);

        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            connectHeaders: { Authorization: `Bearer ${token}` },
            reconnectDelay: 5000,
            onConnect: () => {
                setConnected(true);

                client.subscribe("/user/queue/messages", (msg) => {
                    const body = JSON.parse(msg.body);
                    const otherUser = body.senderId === id ? body.recipientId : body.senderId;

                    // –ï—Å–ª–∏ –ø–æ–ª—É—á–∞—Ç–µ–ª—å –µ—â—ë –Ω–µ –≤—ã–±—Ä–∞–Ω ‚Äî –≤—ã–±–∏—Ä–∞–µ–º –µ–≥–æ –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏
                    if (!recipientIdRef.current) {
                        recipientIdRef.current = otherUser;
                        setRecipientId(otherUser);
                        localStorage.setItem("lastRecipientId", otherUser);
                    }

                    fetchMessages(recipientIdRef.current);
                });
            },
            onStompError: (frame) => {
                console.error("STOMP –æ—à–∏–±–∫–∞: ", frame);
            },
        });

        client.activate();
        clientRef.current = client;

        return () => {
            client.deactivate();
            setConnected(false);
        };
    }, []);

    const handleSend = (e) => {
        e.preventDefault();
        const client = clientRef.current;
        if (!message.trim() || !recipientId.trim()) return;

        if (!client || !client.connected) {
            alert("–ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –Ω–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ.");
            return;
        }

        const payload = {
            senderId: myId,
            recipientId,
            content: message,
            senderName: myName,
            recipientName: "–ò–º—è_–ø–æ–ª—É—á–∞—Ç–µ–ª—è",
        };

        client.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages((prev) => [
            ...prev,
            { sender: "–Ø", text: message, timestamp: new Date().toLocaleString(), senderName: myName },
        ]);
        setMessage("");
    };

    const handleRecipientChange = (e) => {
        const value = e.target.value;
        setRecipientId(value);
        recipientIdRef.current = value;
        localStorage.setItem("lastRecipientId", value);
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                <div className="chat-header">üí¨ –ú–µ—Å—Å–µ–Ω–¥–∂–µ—Ä</div>
                <div className="status">{connected ? "üü¢ –ü–æ–¥–∫–ª—é—á–µ–Ω–æ" : "üî¥ –ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ..."}</div>

                <input
                    type="text"
                    value={recipientId}
                    onChange={handleRecipientChange}
                    placeholder="ID –ø–æ–ª—É—á–∞—Ç–µ–ª—è"
                    className="recipient-input"
                />

                <div className="messages">
                    {messages.map((msg, idx) => (
                        <div key={idx} className="message">
                            <strong>{msg.senderName}:</strong> {msg.text} <span>{msg.timestamp}</span>
                        </div>
                    ))}
                </div>

                <form onSubmit={handleSend} className="input-form">
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ..."
                        className="message-input"
                        required
                    />
                    <button type="submit" className="send-button">
                        –û—Ç–ø—Ä–∞–≤–∏—Ç—å
                    </button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
