import React, { useEffect, useState } from "react";
import { Form, Input, Button, notification } from "antd";
import { DingtalkOutlined } from "@ant-design/icons";
import { signup } from "../util/ApiUtil";
import { useNavigate } from "react-router-dom";
import "./Signup.css";

const Signup = () => {
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        if (localStorage.getItem("accessToken") !== null) {
            navigate("/");
        }
    }, [navigate]);

    const onFinish = (values) => {
        setLoading(true);
        signup(values)
            .then(() => {
                notification.success({
                    message: "Success",
                    description:
                        "Thank you! You're successfully registered. Please log in to continue!",
                });
                navigate("/login");
                setLoading(false);
            })
            .catch((error) => {
                notification.error({
                    message: "Error",
                    description: error.message || "Something went wrong. Please try again!",
                });
                setLoading(false);
            });
    };

    return (
        <div className="login-container">
            <DingtalkOutlined style={{ fontSize: 50 }} />
            <Form
                name="normal_login"
                className="login-form"
                initialValues={{ remember: true }}
                onFinish={onFinish}
            >
                <Form.Item
                    name="name"
                    rules={[{ required: true, message: "Please input your name!" }]}
                >
                    <Input size="large" placeholder="Name" />
                </Form.Item>
                <Form.Item
                    name="username"
                    rules={[{ required: true, message: "Please input your Username!" }]}
                >
                    <Input size="large" placeholder="Username" />
                </Form.Item>
                <Form.Item
                    name="email"
                    rules={[{ required: true, message: "Please input your email!" }]}
                >
                    <Input size="large" placeholder="Email" />
                </Form.Item>
                <Form.Item
                    name="password"
                    rules={[{ required: true, message: "Please input your Password!" }]}
                >
                    <Input size="large" type="password" placeholder="Password" />
                </Form.Item>
                <Form.Item
                    name="profilePicUrl"
                    rules={[
                        {
                            required: true,
                            message: "Please input your profile picture URL!",
                        },
                    ]}
                >
                    <Input size="large" placeholder="Profile picture url" />
                </Form.Item>
                <Form.Item>
                    <Button
                        shape="round"
                        size="large"
                        htmlType="submit"
                        className="login-form-button"
                        loading={loading}
                    >
                        Signup
                    </Button>
                </Form.Item>
                Already a member? <a href="/login">Log in</a>
            </Form>
        </div>
    );
};

export default Signup;
