import React, { useState, useEffect, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { jwtDecode } from "jwt-decode";
import "./Chat.css";

function Chat() {
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);
    const [recipientId, setRecipientId] = useState("");
    const [myId, setMyId] = useState(null);
    const [connected, setConnected] = useState(false);

    const clientRef = useRef(null);
    const recipientIdRef = useRef("");

    const getToken = () => {
        const match = document.cookie.match(new RegExp("(^| )token=([^;]+)"));
        return match ? match[2] : null;
    };

    const fetchMessages = async (recipientIdToFetch) => {
        try {
            const token = getToken();
            const res = await fetch(`/messages/${myId}/${recipientIdToFetch}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            if (res.ok) {
                const data = await res.json();
                setMessages(
                    data.map((msg) => ({
                        sender: msg.senderId === myId ? "–Ø" : msg.senderName || "–û–Ω",
                        text: msg.content,
                        timestamp: new Date(msg.timestamp).toLocaleString(),
                    }))
                );
            } else {
                console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Å–æ–æ–±—â–µ–Ω–∏–π:", res.status);
            }
        } catch (err) {
            console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Å–æ–æ–±—â–µ–Ω–∏–π:", err);
        }
    };

    useEffect(() => {
        if (recipientId && myId) {
            fetchMessages(recipientId);
        }
    }, [recipientId, myId]);

    useEffect(() => {
        const token = getToken();
        if (!token) {
            alert("‚ùå –ù–µ—Ç —Ç–æ–∫–µ–Ω–∞. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–æ–π–¥–∏—Ç–µ —Å–Ω–æ–≤–∞.");
            window.location.href = "/";
            return;
        }

        const decoded = jwtDecode(token);
        const id = decoded.userId;
        if (!id) {
            alert("‚ö†Ô∏è –¢–æ–∫–µ–Ω –Ω–µ —Å–æ–¥–µ—Ä–∂–∏—Ç userId.");
            return;
        }
        setMyId(id);

        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            connectHeaders: { Authorization: `Bearer ${token}` },
            reconnectDelay: 5000,
            onConnect: () => {
                setConnected(true);

                client.subscribe("/user/queue/messages", (msg) => {
                    const body = JSON.parse(msg.body);
                    const otherUser = body.senderId === id ? body.recipientId : body.senderId;

                    if (!recipientIdRef.current) {
                        recipientIdRef.current = otherUser;
                        setRecipientId(otherUser);
                    }

                    const isCurrentChat =
                        (body.senderId === recipientIdRef.current && body.recipientId === id) ||
                        (body.senderId === id && body.recipientId === recipientIdRef.current);

                    if (isCurrentChat) {
                        fetchMessages(recipientIdRef.current);
                    } else {
                        // –ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ –Ω–∞ –Ω–æ–≤–æ–≥–æ —Å–æ–±–µ—Å–µ–¥–Ω–∏–∫–∞
                        recipientIdRef.current = otherUser;
                        setRecipientId(otherUser);
                        fetchMessages(otherUser);
                    }
                });
            },
            onStompError: (frame) => {
                console.error("STOMP –æ—à–∏–±–∫–∞: ", frame);
            },
        });

        client.activate();
        clientRef.current = client;

        return () => {
            client.deactivate();
            setConnected(false);
        };
    }, []);

    const handleSend = (e) => {
        e.preventDefault();
        const client = clientRef.current;
        if (!message.trim() || !recipientId.trim()) return;

        if (!client || !client.connected) {
            alert("‚õî –ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –µ—â—ë –Ω–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ.");
            return;
        }

        const payload = {
            senderId: myId,
            recipientId,
            content: message,
        };

        client.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages((prev) => [
            ...prev,
            {
                sender: "–Ø",
                text: message,
                timestamp: new Date().toLocaleString(),
            },
        ]);
        setMessage("");
    };

    const handleRecipientChange = (e) => {
        const value = e.target.value;
        setRecipientId(value);
        recipientIdRef.current = value;
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                <div className="chat-header">üí¨ –ú–µ—Å—Å–µ–Ω–¥–∂–µ—Ä</div>

                <div className="status">
                    {connected ? "üü¢ –ü–æ–¥–∫–ª—é—á–µ–Ω–æ" : "üî¥ –ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ..."}
                </div>

                <input
                    type="text"
                    value={recipientId}
                    onChange={handleRecipientChange}
                    placeholder="ID –ø–æ–ª—É—á–∞—Ç–µ–ª—è"
                    className="recipient-input"
                />

                <div className="messages">
                    {messages.map((msg, idx) => (
                        <div key={idx} className="message">
                            <div><strong>{msg.sender}</strong> ({msg.timestamp})</div>
                            <div>{msg.text}</div>
                        </div>
                    ))}
                </div>

                <form onSubmit={handleSend} className="input-form">
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ..."
                        className="message-input"
                        required
                    />
                    <button type="submit" className="send-button">–û—Ç–ø—Ä–∞–≤–∏—Ç—å</button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
