        console.log("üì° fetchMessages –≤—ã–∑–≤–∞–Ω –¥–ª—è recipientId:", recipientId);
        try {
            const token = getToken();
            console.log("üõÇ –ò—Å–ø–æ–ª—å–∑—É–µ–º —Ç–æ–∫–µ–Ω:", token);

            const res = await fetch(`/messages/${myId}/${recipientId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            if (res.ok) {
                const data = await res.json();
                console.log("üì® –ó–∞–≥—Ä—É–∂–µ–Ω–æ —Å–æ–æ–±—â–µ–Ω–∏–π:", data.length);
                setMessages(
                    data.map((msg) => ({
                        sender: msg.senderId === myId ? "–Ø" : "–û–Ω",
                        text: msg.content,
                    }))
                );
            } else {
                console.error("‚ùå –û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Å–æ–æ–±—â–µ–Ω–∏–π:", res.status);
            }
        } catch (err) {
            console.error("‚ùå –ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å —Å–æ–æ–±—â–µ–Ω–∏—è:", err);
        }
    };

    useEffect(() => {
        console.log("üîÅ useEffect: [recipientId, myId] —Å—Ä–∞–±–æ—Ç–∞–ª");
        if (recipientId && myId) {
            console.log("üì• –ó–∞–≥—Ä—É–∂–∞–µ–º —Å–æ–æ–±—â–µ–Ω–∏—è –¥–ª—è –ø–æ–ª—É—á–∞—Ç–µ–ª—è:", recipientId);
            fetchMessages(recipientId);
        }
    }, [recipientId, myId]);

    useEffect(() => {
        console.log("üöÄ useEffect: –∏–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è WebSocket");

        const token = getToken();
        if (!token) {
            alert("‚ùå –ù–µ—Ç —Ç–æ–∫–µ–Ω–∞. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–æ–π–¥–∏—Ç–µ —Å–Ω–æ–≤–∞.");
            window.location.href = "/";
            return;
        }

        const decoded = jwtDecode(token);
        const id = decoded.userId;
        const name = decoded.username;
        if (!id) {
            alert("‚ö†Ô∏è –¢–æ–∫–µ–Ω –Ω–µ —Å–æ–¥–µ—Ä–∂–∏—Ç userId. –ù—É–∂–Ω–∞ –∫–æ—Ä—Ä–µ–∫—Ç–∏—Ä–æ–≤–∫–∞ –±—ç–∫–∞.");
            return;
        }
        if (!name) {
            alert("‚ö†Ô∏è –¢–æ–∫–µ–Ω –Ω–µ —Å–æ–¥–µ—Ä–∂–∏—Ç userId. –ù—É–∂–Ω–∞ –∫–æ—Ä—Ä–µ–∫—Ç–∏—Ä–æ–≤–∫–∞ –±—ç–∫–∞.");
            return;
        }
