        setMyId(id);
        setMyName(name);

        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            connectHeaders: { Authorization: `Bearer ${token}` },
            reconnectDelay: 5000,
            onConnect: () => {
                console.log("üü¢ –ü–æ–¥–∫–ª—é—á–µ–Ω–æ –∫ WebSocket");
                console.log("üìå recipientIdRef –ø—Ä–∏ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏–∏:", recipientIdRef.current);
                setConnected(true);

                client.subscribe("/user/queue/messages", (msg) => {
                    const body = JSON.parse(msg.body);
                    console.log("üì® –ù–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ –∏–∑ WebSocket:", body);

                    const otherUser = body.senderId === id ? body.recipientId : body.senderId;

                    if (!recipientIdRef.current) {
                        console.log("üÜï –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º recipientIdRef.current:", otherUser);
                        recipientIdRef.current = otherUser;
                        setRecipientId(otherUser);
                    }

                    const isCurrentChat =
                        (body.senderId === recipientIdRef.current && body.recipientId === id) ||
                        (body.senderId === id && body.recipientId === recipientIdRef.current);

                    if (isCurrentChat) {
                        console.log("üîÑ –û–±–Ω–æ–≤–ª—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏—è –¥–ª—è:", recipientIdRef.current);
                        fetchMessages(recipientIdRef.current);
                    } else {
                        console.log("üì≠ –°–æ–æ–±—â–µ–Ω–∏–µ –Ω–µ –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–æ, —Ç–∞–∫ –∫–∞–∫ –æ—Ç–∫—Ä—ã—Ç –¥—Ä—É–≥–æ–π —á–∞—Ç.");
                    }
                });
            },
            onStompError: (frame) => {
                console.error("üí• STOMP –æ—à–∏–±–∫–∞: ", frame);
            },
        });

        client.activate();
        clientRef.current = client;

        return () => {
            client.deactivate();
            setConnected(false);
        };
    }, []);

    useEffect(() => {
        const intervalId = setInterval(() => {
            console.log("‚è±Ô∏è recipientId –∫–∞–∂–¥—ã–µ 5 —Å–µ–∫:", recipientIdRef.current);
        }, 5000);
        return () => clearInterval(intervalId);
    }, []);

