            senderId: myId,
            recipientId,
            content: message,
        };

        console.log("üì§ –û—Ç–ø—Ä–∞–≤–ª—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ:", payload);

        client.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages((prev) => [...prev, { sender: "–Ø", text: message }]);
        setMessage("");
    };

    const handleRecipientChange = (e) => {
        const value = e.target.value;
        setRecipientId(value);
        recipientIdRef.current = value;
        console.log("‚úèÔ∏è –í–≤–µ–¥—ë–Ω –Ω–æ–≤—ã–π recipientId:", value);
        console.log("üìå –û–±–Ω–æ–≤–ª—ë–Ω recipientIdRef.current:", value);
