import React, { useState, useEffect, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { jwtDecode } from "jwt-decode";
import "./Chat.css";

function Chat() {
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);
    const [recipientId, setRecipientId] = useState("");
    const [myId, setMyId] = useState(null);
    const [myName, setMyName] = useState(""); // Добавлено для имени пользователя
    const [connected, setConnected] = useState(false);

    const clientRef = useRef(null);
    const recipientIdRef = useRef("");

    const getToken = () => {
        const match = document.cookie.match(new RegExp("(^| )token=([^;]+)"));
        const token = match ? match[2] : null;
        console.log("🔑 Получаем токен из cookies:", token);
        return token;
    };

    const fetchMessages = async (recipientId) => {
        console.log("📡 fetchMessages вызван для recipientId:", recipientId);
        try {
            const token = getToken();
            console.log("🛂 Используем токен:", token);

            const res = await fetch(`/messages/${myId}/${recipientId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            if (res.ok) {
                const data = await res.json();
                console.log("📨 Загружено сообщений:", data.length);
                setMessages(
                    data.map((msg) => ({
                        sender: msg.senderId === myId ? "Я" : "Он",
                        text: msg.content,
                        timestamp: msg.timestamp,
                        senderName: msg.senderName, // Добавлено имя отправителя
                    }))
                );
            } else {
                console.error("❌ Ошибка загрузки сообщений:", res.status);
            }
        } catch (err) {
            console.error("❌ Не удалось загрузить сообщения:", err);
        }
    };

    useEffect(() => {
        console.log("🔁 useEffect: [recipientId, myId] сработал");
        if (recipientId && myId) {
            console.log("📥 Загружаем сообщения для получателя:", recipientId);
            fetchMessages(recipientId);
        }
    }, [recipientId, myId]);

    useEffect(() => {
        console.log("🚀 useEffect: инициализация WebSocket");

        const token = getToken();
        if (!token) {
            alert("❌ Нет токена. Пожалуйста, войдите снова.");
            window.location.href = "/";
            return;
        }

        const decoded = jwtDecode(token);
        const id = decoded.userId;
        if (!id) {
            alert("⚠️ Токен не содержит userId. Нужна корректировка бэка.");
            return;
        }
        setMyId(id);
        setMyName(decoded.username); // Получаем имя пользователя из JWT

        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            connectHeaders: { Authorization: `Bearer ${token}` },
            reconnectDelay: 5000,
            onConnect: () => {
                console.log("🟢 Подключено к WebSocket");
                setConnected(true);

                client.subscribe("/user/queue/messages", (msg) => {
                    const body = JSON.parse(msg.body);
                    const otherUser = body.senderId === id ? body.recipientId : body.senderId;

                    if (!recipientIdRef.current) {
                        recipientIdRef.current = otherUser;
                        console.log(otherUser)
                        setRecipientId(otherUser);
                    }

                    fetchMessages(recipientIdRef.current);
                });
            },
            onStompError: (frame) => {
                console.error("💥 STOMP ошибка: ", frame);
            },
        });

        client.activate();
        clientRef.current = client;

        return () => {
            client.deactivate();
            setConnected(false);
        };
    }, []);

    const handleSend = (e) => {
        e.preventDefault();
        const client = clientRef.current;
        if (!message.trim() || !recipientId.trim()) return;

        if (!client || !client.connected) {
            alert("⛔ Подключение ещё не установлено.");
            return;
        }

        const payload = {
            senderId: myId,
            recipientId,
            content: message,
            senderName: myName, // Имя отправителя
            recipientName: "Имя_получателя", // Имя получателя нужно получать или устанавливать вручную
        };

        client.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages((prev) => [
            ...prev,
            { sender: "Я", text: message, timestamp: new Date().toLocaleString() },
        ]);
        setMessage("");
    };

    const handleRecipientChange = (e) => {
        const value = e.target.value;
        setRecipientId(value);
        recipientIdRef.current = value;
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                <div className="chat-header">💬 Мессенджер</div>
                <div className="status">{connected ? "🟢 Подключено" : "🔴 Подключение..."}</div>
                <input
                    type="text"
                    value={recipientId}
                    onChange={handleRecipientChange}
                    placeholder="ID получателя"
                    className="recipient-input"
                />
                <div className="messages">
                    {messages.map((msg, idx) => (
                        <div key={idx} className="message">
                            <strong>{msg.senderName}:</strong> {msg.text} <span>{msg.timestamp}</span>
                        </div>
                    ))}
                </div>
                <form onSubmit={handleSend} className="input-form">
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Введите сообщение..."
                        className="message-input"
                        required
                    />
                    <button type="submit" className="send-button">
                        Отправить
                    </button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
