import React, { useEffect, useState } from "react";
import { Button, message } from "antd";
import {
    getUsers,
    countNewMessages,
    findChatMessages,
    findChatMessage,
} from "./api";
import { useRecoilValue, useRecoilState } from "recoil";
import {
    loggedInUser,
    chatActiveContact,
    chatMessages,
} from "./globalState";
import ScrollToBottom from "react-scroll-to-bottom";
import "./Chat.css";

let stompClient = null;

const Chat = (props) => {
    const currentUser = useRecoilValue(loggedInUser);
    const [text, setText] = useState("");
    const [contacts, setContacts] = useState([]);
    const [activeContact, setActiveContact] = useRecoilState(chatActiveContact);
    const [messages, setMessages] = useRecoilState(chatMessages);

    useEffect(() => {
        if (localStorage.getItem("accessToken") === null) {
            props.history.push("/login");
        }
        connect();
        loadContacts();
    }, []);

    useEffect(() => {
        if (!activeContact) return;
        findChatMessages(activeContact.id, currentUser.id).then((msgs) =>
            setMessages(msgs)
        );
        loadContacts();
    }, [activeContact]);

    const connect = () => {
        const Stomp = require("stompjs");
        let SockJS = require("sockjs-client");
        SockJS = new SockJS("http://localhost:8080/ws");
        stompClient = Stomp.over(SockJS);
        stompClient.connect({}, onConnected, onError);
    };

    const onConnected = () => {
        console.log("âœ… WebSocket connected");
        stompClient.subscribe(
            `/user/${currentUser.id}/queue/messages`,
            onMessageReceived
        );
    };

    const onError = (err) => {
        console.error("WebSocket error", err);
    };

    const onMessageReceived = (msg) => {
        const notification = JSON.parse(msg.body);
        console.log("ðŸ”” New message received", notification);

        if (activeContact && activeContact.id === notification.senderId) {
            findChatMessage(notification.id).then((messageData) => {
                setMessages((prevMessages) => [...prevMessages, messageData]);
            });
        } else {
            message.info("ÐÐ¾Ð²Ð¾Ðµ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ðµ Ð¾Ñ‚ " + notification.senderName);
        }

        loadContacts();
    };

    const sendMessage = (msg) => {
        if (msg.trim() !== "") {
            const messageData = {
                senderId: currentUser.id,
                recipientId: activeContact.id,
                senderName: currentUser.name,
                recipientName: activeContact.name,
                content: msg,
                timestamp: new Date(),
            };

            stompClient.send("/app/chat", {}, JSON.stringify(messageData));
            setMessages((prevMessages) => [...prevMessages, messageData]);
        }
    };

    const loadContacts = () => {
        getUsers().then((users) => {
            const userPromises = users.map((user) =>
                countNewMessages(user.id, currentUser.id).then((count) => {
                    user.newMessages = count;
                    return user;
                })
            );

            Promise.all(userPromises).then((contacts) => {
                setContacts(contacts);
                if (!activeContact && contacts.length > 0) {
                    setActiveContact(contacts[0]);
                }
            });
        });
    };

    return (
        <div id="frame">
            <div id="sidepanel">
                <div id="profile">
                    <div className="wrap">
                        <img
                            id="profile-img"
                            src={currentUser.profilePicture}
                            className="online"
                            alt=""
                        />
                        <p>{currentUser.name}</p>
                    </div>
                </div>

                <div id="search" />
                <div id="contacts">
                    <ul>
                        {contacts.map((contact) => (
                            <li
                                key={contact.id}
                                onClick={() => setActiveContact(contact)}
                                className={
                                    activeContact && contact.id === activeContact.id
                                        ? "contact active"
                                        : "contact"
                                }
                            >
                                <div className="wrap">
                                    <span className="contact-status online"></span>
                                    <img src={contact.profilePicture} alt="" />
                                    <div className="meta">
                                        <p className="name">{contact.name}</p>
                                        {contact.newMessages > 0 && (
                                            <p className="preview">
                                                {contact.newMessages} new messages
                                            </p>
                                        )}
                                    </div>
                                </div>
                            </li>
                        ))}
                    </ul>
                </div>

                <div id="bottom-bar">
                    <button id="addcontact">
                        <i className="fa fa-user fa-fw" aria-hidden="true"></i>{" "}
                        <span>Profile</span>
                    </button>
                    <button id="settings">
                        <i className="fa fa-cog fa-fw" aria-hidden="true"></i>{" "}
                        <span>Settings</span>
                    </button>
                </div>
            </div>

            <div className="content">
                <div className="contact-profile">
                    <img src={activeContact?.profilePicture} alt="" />
                    <p>{activeContact?.name}</p>
                </div>

                <ScrollToBottom className="messages">
                    <ul>
                        {messages.map((msg, idx) => (
                            <li
                                key={idx}
                                className={msg.senderId === currentUser.id ? "sent" : "replies"}
                            >
                                {msg.senderId !== currentUser.id && (
                                    <img src={activeContact.profilePicture} alt="" />
                                )}
                                <p>{msg.content}</p>
                            </li>
                        ))}
                    </ul>
                </ScrollToBottom>

                <div className="message-input">
                    <div className="wrap">
                        <input
                            name="user_input"
                            placeholder="Write your message..."
                            value={text}
                            onChange={(e) => setText(e.target.value)}
                            onKeyPress={(e) => {
                                if (e.key === "Enter") {
                                    sendMessage(text);
                                    setText("");
                                }
                            }}
                        />
                        <Button
                            icon={<i className="fa fa-paper-plane" aria-hidden="true"></i>}
                            onClick={() => {
                                sendMessage(text);
                                setText("");
                            }}
                        />
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Chat;
