import React, { useState } from 'react';
import Chat from './Chat';

function App() {
    const users = [
        { id: '1', name: 'Alice' },
        { id: '2', name: 'Bob' }
    ];

    const [currentUser, setCurrentUser] = useState(null);
    const [recipient, setRecipient] = useState(null);

    if (!currentUser) {
        return (
            <div>
                <h2>Select your user</h2>
                {users.map((u) => (
                    <button key={u.id} onClick={() => setCurrentUser(u)}>
                        {u.name}
                    </button>
                ))}
            </div>
        );
    }

    if (!recipient) {
        return (
            <div>
                <h2>Chat as {currentUser.name}. Choose recipient:</h2>
                {users
                    .filter((u) => u.id !== currentUser.id)
                    .map((u) => (
                        <button key={u.id} onClick={() => setRecipient(u)}>
                            {u.name}
                        </button>
                    ))}
            </div>
        );
    }

    return <Chat currentUser={currentUser} recipient={recipient} />;
}

export default App;
