import React, { useEffect, useState, useRef } from "react";
import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs";

const Chat = () => {
    const [myId, setMyId] = useState(null);
    const [recipientId, setRecipientId] = useState("");
    const [messages, setMessages] = useState([]);
    const [messageText, setMessageText] = useState("");
    const [status, setStatus] = useState("üî¥ –ù–µ –ø–æ–¥–∫–ª—é—á–µ–Ω–æ");

    const stompRef = useRef(null);

    const getToken = () => {
        const match = document.cookie.match(/(^| )token=([^;]+)/);
        return match ? match[2] : null;
    };

    const connectWebSocket = (token) => {
        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            connectHeaders: { Authorization: `Bearer ${token}` },
            onConnect: () => {
                setStatus("üü¢ –ü–æ–¥–∫–ª—é—á–µ–Ω–æ");
                client.subscribe("/user/queue/messages", (msg) => {
                    const payload = JSON.parse(msg.body);
                    setMessages((prev) => [...prev, payload]);
                });
            },
            onStompError: (frame) => {
                console.error("STOMP error:", frame);
                setStatus("‚õî –û—à–∏–±–∫–∞ –ø–æ–¥–∫–ª—é—á–µ–Ω–∏—è");
            },
            reconnectDelay: 5000,
        });

        client.activate();
        stompRef.current = client;
    };

    const loadMyId = async () => {
        const token = getToken();
        if (!token) {
            alert("‚õî –ù–µ—Ç —Ç–æ–∫–µ–Ω–∞. –í–æ–π–¥–∏—Ç–µ —Å–Ω–æ–≤–∞.");
            window.location.href = "/";
            return;
        }

        try {
            const res = await fetch("/auth/me", {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            if (!res.ok) throw new Error("–û—à–∏–±–∫–∞ –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏");
            const data = await res.json();
            setMyId(data.userId);
            connectWebSocket(token);
        } catch (err) {
            alert("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø—Ä–æ—Ñ–∏–ª—è: " + err.message);
        }
    };

    const loadMessages = async () => {
        if (!myId || !recipientId) return;
        const token = getToken();
        try {
            const res = await fetch(`/messages/${myId}/${recipientId}`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            if (!res.ok) throw new Error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Å–æ–æ–±—â–µ–Ω–∏–π");
            const data = await res.json();
            setMessages(data);
        } catch (err) {
            console.error("–û—à–∏–±–∫–∞:", err);
        }
    };

    const sendMessage = (e) => {
        e.preventDefault();
        if (!messageText.trim() || !stompRef.current || !recipientId) return;

        const payload = {
            senderId: myId,
            recipientId,
            content: messageText.trim(),
        };

        stompRef.current.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages((prev) => [...prev, payload]);
        setMessageText("");
    };

    useEffect(() => {
        loadMyId();
    }, []);

    useEffect(() => {
        loadMessages();
    }, [recipientId, myId]);

    return (
        <div style={{ maxWidth: 500, margin: "0 auto", padding: 20 }}>
            <h2>üí¨ –ß–∞—Ç</h2>
            <div>
                <label>ID –ø–æ–ª—É—á–∞—Ç–µ–ª—è:</label>
                <input
                    value={recipientId}
                    onChange={(e) => setRecipientId(e.target.value)}
                    placeholder="user123"
                    style={{ width: "100%", marginBottom: 10 }}
                />
            </div>

            <div
                style={{
                    height: 200,
                    overflowY: "auto",
                    border: "1px solid #ccc",
                    padding: 10,
                    marginBottom: 10,
                }}
            >
                {messages.map((msg, i) => (
                    <div key={i} style={{ marginBottom: 5 }}>
                        <strong>{msg.senderId === myId ? "–Ø" : "–û–Ω"}:</strong>{" "}
                        {msg.content}
                    </div>
                ))}
            </div>

            <form onSubmit={sendMessage} style={{ display: "flex", gap: 10 }}>
                <input
                    value={messageText}
                    onChange={(e) => setMessageText(e.target.value)}
                    placeholder="–°–æ–æ–±—â–µ–Ω–∏–µ..."
                    style={{ flex: 1 }}
                />
                <button type="submit">–û—Ç–ø—Ä–∞–≤–∏—Ç—å</button>
            </form>

            <div style={{ marginTop: 10 }}>{status}</div>
        </div>
    );
};

export default Chat;
