import React, { useState } from "react";

function SignUp() {
    const [form, setForm] = useState({ username: "", email: "", password: "" });

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await fetch("http://localhost:8080/auth/sign-up", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(form),
                credentials: "include",
            });

            const data = await res.json();
            if (res.ok) {
                alert("✅ Успішна реєстрація!");
                window.location.href = "/chat";
            } else {
                alert("❌ Помилка: " + data.message);
            }
        } catch (err) {
            alert("❌ Сервер не відповідає.");
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h2>Реєстрація</h2>
            <input name="username" placeholder="Нікнейм" value={form.username} onChange={handleChange} required />
            <input name="email" type="email" placeholder="Email" value={form.email} onChange={handleChange} required />
            <input name="password" type="password" placeholder="Пароль" value={form.password} onChange={handleChange} required />
            <button type="submit">Зареєструватися</button>
        </form>
    );
}

export default SignUp;
