import React, { useState, useEffect, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { jwtDecode } from "jwt-decode";
import "./Chat.css";

function Chat() {
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);
    const [recipientId, setRecipientId] = useState("");
    const [myId, setMyId] = useState(null);
    const [myName, setMyName] = useState("");
    const [connected, setConnected] = useState(false);

    const clientRef = useRef(null);
    const recipientIdRef = useRef("");

    const getToken = () => {
        const match = document.cookie.match(new RegExp("(^| )token=([^;]+)"));
        return match ? match[2] : null;
    };

    const fetchMessages = async (recipientId) => {
        if (!myId || !recipientId) {
            console.warn("fetchMessages: myId или recipientId отсутствует", { myId, recipientId });
            return;
        }

        try {
            const token = getToken();
            const res = await fetch(`/messages/${myId}/${recipientId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            if (res.ok) {
                const data = await res.json();
                setMessages(
                    data.map((msg) => ({
                        sender: msg.senderId === myId ? "Я" : "Он",
                        text: msg.content,
                        timestamp: msg.timestamp,
                        senderName: msg.senderName,
                    }))
                );
            } else {
                console.error("Ошибка загрузки сообщений:", res.status);
            }
        } catch (err) {
            console.error("Не удалось загрузить сообщения:", err);
        }
    };

    // Устанавливаем myId и myName после получения токена
    useEffect(() => {
        const token = getToken();
        if (!token) {
            alert("Нет токена. Войдите снова.");
            window.location.href = "/";
            return;
        }

        const decoded = jwtDecode(token);
        const id = decoded.userId;
        const name = decoded.username;
        setMyId(id);
        setMyName(name);
    }, []);

    // После установки myId — подгружаем recipientId из localStorage
    useEffect(() => {
        if (!myId) return;
        const saved = localStorage.getItem("lastRecipientId");
        if (saved) {
            setRecipientId(saved);
            recipientIdRef.current = saved;
        }
    }, [myId]);

    // Загружаем историю сообщений
    useEffect(() => {
        if (recipientId && myId) {
            console.log("Загрузка истории для:", { myId, recipientId });
            fetchMessages(recipientId);
            localStorage.setItem("lastRecipientId", recipientId);
        }
    }, [recipientId, myId]);

    // WebSocket инициализация
    useEffect(() => {
        const token = getToken();
        if (!token) return;

        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            connectHeaders: { Authorization: `Bearer ${token}` },
            reconnectDelay: 5000,
            onConnect: () => {
                setConnected(true);
                client.subscribe("/user/queue/messages", (msg) => {
                    const body = JSON.parse(msg.body);
                    const otherUser = body.senderId === myId ? body.recipientId : body.senderId;

                    console.log("📩 Новое сообщение от:", otherUser);

                    if (!recipientIdRef.current) {
                        recipientIdRef.current = otherUser;
                        setRecipientId(otherUser);
                        localStorage.setItem("lastRecipientId", otherUser);
                    }

                    fetchMessages(recipientIdRef.current);
                });
            },
            onStompError: (frame) => {
                console.error("STOMP ошибка:", frame);
            },
        });

        client.activate();
        clientRef.current = client;

        return () => {
            client.deactivate();
            setConnected(false);
        };
    }, [myId]);

    const handleSend = (e) => {
        e.preventDefault();
        const client = clientRef.current;
        if (!message.trim() || !recipientId.trim()) return;

        if (!client || !client.connected) {
            alert("Подключение не установлено.");
            return;
        }

        const payload = {
            senderId: myId,
            recipientId,
            content: message,
            senderName: myName,
            recipientName: "Имя_получателя", // временно
        };

        client.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages((prev) => [
            ...prev,
            {
                sender: "Я",
                text: message,
                timestamp: new Date().toLocaleString(),
                senderName: myName,
            },
        ]);
        setMessage("");
    };

    const handleRecipientChange = (e) => {
        const value = e.target.value;
        setRecipientId(value);
        recipientIdRef.current = value;
        localStorage.setItem("lastRecipientId", value);
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                <div className="chat-header">💬 Мессенджер</div>
                <div className="status">{connected ? "🟢 Подключено" : "🔴 Подключение..."}</div>

                <input
                    type="text"
                    value={recipientId}
                    onChange={handleRecipientChange}
                    placeholder="ID получателя"
                    className="recipient-input"
                />

                <div className="messages">
                    {messages.map((msg, idx) => (
                        <div key={idx} className="message">
                            <strong>{msg.senderName}:</strong> {msg.text} <span>{msg.timestamp}</span>
                        </div>
                    ))}
                </div>

                <form onSubmit={handleSend} className="input-form">
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Введите сообщение..."
                        className="message-input"
                        required
                    />
                    <button type="submit" className="send-button">Отправить</button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
