import React, { useEffect, useState } from "react";
import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs";
import "./Chat.css";

function Chat() {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const [stompClient, setStompClient] = useState(null);

    const currentUser = {
        id: localStorage.getItem("userId"),
        name: localStorage.getItem("username") || "–Ø"
    };

    // –ü—Ä–∏–º–µ—Ä: –∞–∫—Ç–∏–≤–Ω—ã–π –∫–æ–Ω—Ç–∞–∫—Ç (–≤—ã–±—Ä–∞—Ç—å –ø–æ–∑–∂–µ —á–µ—Ä–µ–∑ UI)
    const activeContact = {
        id: "2",
        name: "–î—Ä—É–≥"
    };

    useEffect(() => {
        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            reconnectDelay: 5000,
            onConnect: () => {
                console.log("‚úÖ –ü–æ–¥–∫–ª—é—á–µ–Ω–æ –∫ WebSocket");
                client.subscribe(
                    `/user/${currentUser.id}/queue/messages`,
                    (msg) => {
                        const body = JSON.parse(msg.body);
                        setMessages(prev => [...prev, {
                            sender: body.senderName,
                            text: body.content
                        }]);
                    }
                );
            },
            onStompError: (frame) => {
                console.error("–û—à–∏–±–∫–∞ –±—Ä–æ–∫–µ—Ä–∞ STOMP:", frame.headers["message"]);
                console.error("–ü–æ–¥—Ä–æ–±–Ω–æ—Å—Ç–∏:", frame.body);
            }
        });

        client.activate();
        setStompClient(client);

        return () => {
            if (client && client.active) client.deactivate();
        };
    }, [currentUser.id]);

    const sendMessage = (e) => {
        e.preventDefault();
        if (input.trim() === "" || !stompClient || !stompClient.connected) return;

        const msg = {
            senderId: currentUser.id,
            senderName: currentUser.name,
            recipientId: activeContact.id,
            recipientName: activeContact.name,
            content: input,
            timestamp: new Date()
        };

        stompClient.publish({
            destination: "/app/chat",
            body: JSON.stringify(msg)
        });

        setMessages(prev => [...prev, { sender: "–Ø", text: input }]);
        setInput("");
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                <div className="chat-header">üí¨ –ß–∞—Ç —Å {activeContact.name}</div>
                <div className="messages">
                    {messages.map((msg, idx) => (
                        <div key={idx} className="message">
                            <strong>{msg.sender}:</strong> {msg.text}
                        </div>
                    ))}
                </div>
                <form onSubmit={sendMessage} className="input-form">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ..."
                        className="message-input"
                        required
                    />
                    <button type="submit" className="send-button">–û—Ç–ø—Ä–∞–≤–∏—Ç—å</button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
