import React, { useEffect, useState } from "react";
import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs";
import jwt_decode from "jwt-decode";
import "./Chat.css";

function Chat() {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const [stompClient, setStompClient] = useState(null);
    const [currentUser, setCurrentUser] = useState(null);

    const activeContact = {
        id: "2", // –¥–æ–ø—É—Å—Ç–∏–º, —Ç—ã –≤—Å–µ–≥–¥–∞ —á–∞—Ç–∏—à—å—Å—è —Å Test2 –¥–ª—è –ø—Ä–∏–º–µ—Ä–∞
        name: "Test2"
    };

    // –§—É–Ω–∫—Ü–∏—è –ø–æ–ª—É—á–µ–Ω–∏—è —Ç–æ–∫–µ–Ω–∞ –∏–∑ cookies
    function getCurrentUserFromToken() {
        const match = document.cookie.match(new RegExp('(^| )token=([^;]+)'));
        if (match) {
            const token = match[2];
            const decoded = jwt_decode(token);
            return {
                id: decoded.userId?.toString(),
                name: decoded.username
            };
        }
        return null;
    }

    useEffect(() => {
        const user = getCurrentUserFromToken();
        if (!user) {
            console.error("–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–µ –Ω–∞–π–¥–µ–Ω –≤ —Ç–æ–∫–µ–Ω–µ");
            return;
        }
        setCurrentUser(user);

        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            reconnectDelay: 5000,
            onConnect: () => {
                console.log("‚úÖ –ü–æ–¥–∫–ª—é—á–µ–Ω–æ –∫ WebSocket –∫–∞–∫", user);
                client.subscribe(
                    `/user/${user.id}/queue/messages`,
                    (msg) => {
                        const body = JSON.parse(msg.body);
                        setMessages(prev => [...prev, {
                            sender: body.senderName,
                            text: body.content
                        }]);
                    }
                );
            },
            onStompError: (frame) => {
                console.error("–û—à–∏–±–∫–∞ –±—Ä–æ–∫–µ—Ä–∞ STOMP:", frame.headers["message"]);
                console.error("–ü–æ–¥—Ä–æ–±–Ω–æ—Å—Ç–∏:", frame.body);
            }
        });

        client.activate();
        setStompClient(client);

        return () => {
            if (client && client.active) client.deactivate();
        };
    }, []);

    const sendMessage = (e) => {
        e.preventDefault();
        if (input.trim() === "" || !stompClient || !stompClient.connected || !currentUser) return;

        const msg = {
            senderId: currentUser.id,
            senderName: currentUser.name,
            recipientId: activeContact.id,
            recipientName: activeContact.name,
            content: input,
            timestamp: new Date()
        };

        stompClient.publish({
            destination: "/app/chat",
            body: JSON.stringify(msg)
        });

        setMessages(prev => [...prev, { sender: "–Ø", text: input }]);
        setInput("");
    };

    if (!currentUser) {
        return <div>–ó–∞–≥—Ä—É–∑–∫–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è...</div>;
    }

    return (
        <div className="chat-container">
            <div className="chat-box">
                <div className="chat-header">üí¨ –ß–∞—Ç —Å {activeContact.name}</div>
                <div className="messages">
                    {messages.map((msg, idx) => (
                        <div key={idx} className="message">
                            <strong>{msg.sender}:</strong> {msg.text}
                        </div>
                    ))}
                </div>
                <form onSubmit={sendMessage} className="input-form">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ..."
                        className="message-input"
                        required
                    />
                    <button type="submit" className="send-button">–û—Ç–ø—Ä–∞–≤–∏—Ç—å</button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
