import React, { useState, useEffect, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { jwtDecode } from "jwt-decode";
import "./Chat.css";

function Chat() {
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);
    const [recipientEmail, setRecipientEmail] = useState("");
    const [recipientUserId, setRecipientUserId] = useState("");
    const [myId, setMyId] = useState(null);
    const [connected, setConnected] = useState(false);
    const clientRef = useRef(null);

    const getToken = () => {
        const match = document.cookie.match(new RegExp("(^| )token=([^;]+)"));
        return match ? match[2] : null;
    };

    const fetchMessages = async (recipientId) => {
        try {
            const token = getToken();
            const res = await fetch(`/messages/${myId}/${recipientId}`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            if (res.ok) {
                const data = await res.json();
                setMessages(
                    data.map((msg) => ({
                        sender: msg.senderId === myId ? "Я" : "Он",
                        text: msg.content,
                    }))
                );
            } else {
                console.error("Ошибка загрузки сообщений:", res.status);
            }
        } catch (err) {
            console.error("Не удалось загрузить сообщения", err);
        }
    };

    const findUserIdByEmail = async (email) => {
        const token = getToken();
        try {
            const res = await fetch(`/users/findByEmail?email=${encodeURIComponent(email)}`, {
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            if (res.ok) {
                const data = await res.json();
                return data.id;
            } else {
                console.error("Не удалось найти пользователя по email:", res.status);
            }
        } catch (err) {
            console.error("Ошибка при поиске по email:", err);
        }
        return null;
    };

    useEffect(() => {
        const token = getToken();
        if (!token) {
            alert("❌ Нет токена. Пожалуйста, войдите снова.");
            window.location.href = "/";
            return;
        }

        const decoded = jwtDecode(token);
        const id = decoded.userId;
        if (!id) {
            alert("⚠️ Токен не содержит userId.");
            return;
        }
        setMyId(id);

        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            connectHeaders: { Authorization: `Bearer ${token}` },
            onConnect: () => {
                console.log("🟢 Подключено к WebSocket");
                setConnected(true);

                client.subscribe("/user/queue/messages", (msg) => {
                    const body = JSON.parse(msg.body);
                    if (
                        (body.senderId === recipientUserId && body.recipientId === myId) ||
                        (body.senderId === myId && body.recipientId === recipientUserId)
                    ) {
                        setMessages((prev) => [
                            ...prev,
                            {
                                sender: body.senderId === myId ? "Я" : "Он",
                                text: body.content,
                            },
                        ]);
                    }
                });
            },
            onStompError: (frame) => {
                console.error("STOMP ошибка: ", frame);
            },
            reconnectDelay: 5000,
        });

        client.activate();
        clientRef.current = client;

        return () => {
            client.deactivate();
            setConnected(false);
        };
    }, []);

    const handleSend = (e) => {
        e.preventDefault();
        if (!message.trim() || !recipientUserId.trim()) return;

        const client = clientRef.current;

        if (!client || !client.connected) {
            alert("⛔ Подключение ещё не установлено.");
            return;
        }

        const payload = {
            senderId: myId,
            recipientId: recipientUserId,
            content: message,
        };

        client.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages((prev) => [...prev, { sender: "Я", text: message }]);
        setMessage("");
    };

    const handleRecipientChange = async (e) => {
        const email = e.target.value;
        setRecipientEmail(email);
        if (email && myId) {
            const userId = await findUserIdByEmail(email);
            if (userId) {
                setRecipientUserId(userId);
                fetchMessages(userId);
            } else {
                alert("❌ Пользователь не найден.");
            }
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                <div className="chat-header">💬 Мессенджер</div>

                <div className="status">
                    {connected ? "🟢 Подключено" : "🔴 Подключение..."}
                </div>

                <input
                    type="email"
                    value={recipientEmail}
                    onChange={handleRecipientChange}
                    placeholder="Email получателя"
                    className="recipient-input"
                />

                <div className="messages">
                    {messages.map((msg, idx) => (
                        <div key={idx} className="message">
                            <strong>{msg.sender}:</strong> {msg.text}
                        </div>
                    ))}
                </div>

                <form onSubmit={handleSend} className="input-form">
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Введите сообщение..."
                        className="message-input"
                        required
                    />
                    <button type="submit" className="send-button">
                        Отправить
                    </button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
