import React, { useState, useEffect, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { jwtDecode } from "jwt-decode";
import "./Chat.css";

function Chat() {
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);
    const [recipientId, setRecipientId] = useState("");
    const [myId, setMyId] = useState(null);
    const [myName, setMyName] = useState("");
    const [connected, setConnected] = useState(false);
    const [allUsers, setAllUsers] = useState([]);
    const [isActive, setIsActive] = useState(true); // Track if the window is active

    const clientRef = useRef(null);
    const recipientIdRef = useRef("");

    const getToken = () => {
        const match = document.cookie.match(new RegExp("(^| )token=([^;]+)"));
        return match ? match[2] : null;
    };

    const fetchMessages = async (recipientId) => {
        try {
            const token = getToken();
            const res = await fetch(`/messages/${myId}/${recipientId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            if (res.ok) {
                const data = await res.json();
                setMessages(data.map((msg) => ({
                    sender: msg.senderId === myId ? "–Ø" : "–û–Ω",
                    text: msg.content,
                    timestamp: msg.timestamp,
                    senderName: msg.senderName,
                })));
            } else {
                console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Å–æ–æ–±—â–µ–Ω–∏–π:", res.status);
            }
        } catch (err) {
            console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —Å–æ–æ–±—â–µ–Ω–∏–π:", err);
        }
    };

    useEffect(() => {
        if (recipientId && myId) {
            fetchMessages(recipientId);
        }
    }, [recipientId, myId]);

    useEffect(() => {
        const token = getToken();
        if (!token) {
            alert("–ù–µ—Ç —Ç–æ–∫–µ–Ω–∞. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–æ–π–¥–∏—Ç–µ —Å–Ω–æ–≤–∞.");
            window.location.href = "/";
            return;
        }

        const decoded = jwtDecode(token);
        const id = decoded.userId;
        if (!id) {
            alert("–¢–æ–∫–µ–Ω –Ω–µ —Å–æ–¥–µ—Ä–∂–∏—Ç userId.");
            return;
        }
        setMyId(id);
        setMyName(decoded.username);

        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            connectHeaders: { Authorization: `Bearer ${token}` },
            reconnectDelay: 5000,
            onConnect: () => {
                setConnected(true);
                client.subscribe(`/user/${id}/queue/messages`, (msg) => {
                    const body = JSON.parse(msg.body);
                    const otherUserId = body.senderId;

                    // –û–±–Ω–æ–≤–ª—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏—è, –µ—Å–ª–∏ —ç—Ç–æ —Å–æ–æ–±—â–µ–Ω–∏–µ –æ—Ç –≤—ã–±—Ä–∞–Ω–Ω–æ–≥–æ —Å–æ–±–µ—Å–µ–¥–Ω–∏–∫–∞
                    if (recipientIdRef.current === otherUserId) {
                        setMessages(prevMessages => [
                            ...prevMessages,
                            {
                                sender: body.senderId === myId ? "–Ø" : "–û–Ω",
                                text: body.content,
                                timestamp: body.timestamp,
                                senderName: body.senderName,
                            }
                        ]);
                    } else {
                        // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º —É–≤–µ–¥–æ–º–ª–µ–Ω–∏–µ, –µ—Å–ª–∏ —á–∞—Ç –Ω–µ –∞–∫—Ç–∏–≤–µ–Ω
                        if (!isActive) {
                            new Notification("–ù–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ", {
                                body: `${body.senderName}: ${body.content}`,
                            });
                        } else {
                            console.log(`üì© –ù–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ –æ—Ç ${body.senderName}`);
                        }
                    }
                });
            },
            onStompError: (frame) => {
                console.error("STOMP –æ—à–∏–±–∫–∞:", frame);
            },
        });

        client.activate();
        clientRef.current = client;

        return () => {
            client.deactivate();
            setConnected(false);
        };
    }, [isActive]);

    useEffect(() => {
        // Request permission for notifications if not granted
        if (Notification.permission !== "granted") {
            Notification.requestPermission();
        }

        // Check if the page is active
        const handleVisibilityChange = () => {
            setIsActive(!document.hidden);
        };

        document.addEventListener("visibilitychange", handleVisibilityChange);

        return () => {
            document.removeEventListener("visibilitychange", handleVisibilityChange);
        };
    }, []);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const token = getToken();
                const res = await fetch("/users/all", {
                    headers: { Authorization: `Bearer ${token}` },
                });

                if (res.ok) {
                    const data = await res.json();
                    setAllUsers(data.filter((user) => user.id !== myId));
                } else {
                    console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π:", res.status);
                }
            } catch (err) {
                console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π:", err);
            }
        };

        if (myId) {
            fetchUsers();
        }
    }, [myId]);

    const handleSend = (e) => {
        e.preventDefault();
        const client = clientRef.current;
        if (!message.trim() || !recipientId.trim()) return;

        if (!client || !client.connected) {
            alert("–ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –µ—â—ë –Ω–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ.");
            return;
        }

        const recipientUser = allUsers.find((u) => u.id === recipientId);
        const recipientName = recipientUser ? recipientUser.username : "–ü–æ–ª—É—á–∞—Ç–µ–ª—å";

        const payload = {
            senderId: myId,
            recipientId,
            content: message,
            senderName: myName,
            recipientName: recipientName,
            timestamp: new Date().toISOString(),
        };

        client.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages((prev) => [
            ...prev,
            {
                sender: "–Ø",
                text: message,
                timestamp: new Date().toLocaleString(),
                senderName: myName,
            },
        ]);
        setMessage("");
    };

    const handleRecipientChange = (e) => {
        const value = e.target.value;
        recipientIdRef.current = value;
        setRecipientId(value);
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                <div className="chat-header">üí¨ –ú–µ—Å—Å–µ–Ω–¥–∂–µ—Ä</div>
                <div className="status">
                    {connected ? "üü¢ –ü–æ–¥–∫–ª—é—á–µ–Ω–æ" : "üî¥ –ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ..."}
                </div>
                <select
                    value={recipientId}
                    onChange={handleRecipientChange}
                    className="recipient-input"
                >
                    <option value="">-- –í—ã–±–µ—Ä–∏—Ç–µ –ø–æ–ª—É—á–∞—Ç–µ–ª—è --</option>
                    {allUsers.map((user) => (
                        <option key={user.id} value={user.id}>
                            {user.username} ({user.email})
                        </option>
                    ))}
                </select>
                <div className="messages">
                    {messages.map((msg, idx) => (
                        <div key={idx} className="message">
                            <strong>{msg.senderName}:</strong> {msg.text}{" "}
                            <span>{msg.timestamp}</span>
                        </div>
                    ))}
                </div>
                <form onSubmit={handleSend} className="input-form">
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ..."
                        className="message-input"
                        required
                    />
                    <button type="submit" className="send-button">
                        –û—Ç–ø—Ä–∞–≤–∏—Ç—å
                    </button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
