import React, { useState, useEffect, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { jwtDecode } from "jwt-decode";
import "./Chat.css";

function Chat() {
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);
    const [recipientId, setRecipientId] = useState("");
    const [fixedRecipient, setFixedRecipient] = useState(null);
    const [myId, setMyId] = useState(null);
    const [connected, setConnected] = useState(false);
    const clientRef = useRef(null);

    const getToken = () => {
        const match = document.cookie.match(new RegExp("(^| )token=([^;]+)"));
        return match ? match[2] : null;
    };

    const fetchMessages = async (recipientId) => {
        try {
            const token = getToken();
            const res = await fetch(`/messages/${myId}/${recipientId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            if (res.ok) {
                const data = await res.json();
                setMessages(
                    data.map((msg) => ({
                        sender: msg.senderId === myId ? "Я" : "Он",
                        text: msg.content,
                    }))
                );
            } else {
                console.error("Ошибка загрузки сообщений:", res.status);
            }
        } catch (err) {
            console.error("Не удалось загрузить сообщения", err);
        }
    };

    useEffect(() => {
        const token = getToken();
        if (!token) {
            alert("❌ Нет токена. Пожалуйста, войдите снова.");
            window.location.href = "/";
            return;
        }

        const decoded = jwtDecode(token);
        const id = decoded.userId;
        if (!id) {
            alert("⚠️ Токен не содержит userId.");
            return;
        }
        setMyId(id);

        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            connectHeaders: { Authorization: `Bearer ${token}` },
            onConnect: () => {
                console.log("🟢 Подключено к WebSocket");
                setConnected(true);

                client.subscribe("/user/queue/messages", (msg) => {
                    const body = JSON.parse(msg.body);
                    console.log("📨 Получено сообщение:", body);

                    if (
                        fixedRecipient &&
                        ((body.senderId === fixedRecipient && body.recipientId === myId) ||
                            (body.senderId === myId && body.recipientId === fixedRecipient))
                    ) {
                        fetchMessages(fixedRecipient);
                    }
                });
            },
            onStompError: (frame) => {
                console.error("STOMP ошибка:", frame);
            },
            reconnectDelay: 5000,
        });

        client.activate();
        clientRef.current = client;

        return () => {
            client.deactivate();
            setConnected(false);
        };
    }, []);

    const handleSetRecipient = () => {
        if (recipientId.trim()) {
            setFixedRecipient(recipientId.trim());
            fetchMessages(recipientId.trim());
        }
    };

    const handleSend = (e) => {
        e.preventDefault();
        if (!message.trim() || !fixedRecipient) return;

        const client = clientRef.current;
        if (!client || !client.connected) {
            alert("⛔ Подключение ещё не установлено.");
            return;
        }

        const payload = {
            senderId: myId,
            recipientId: fixedRecipient,
            content: message,
        };

        client.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages((prev) => [...prev, { sender: "Я", text: message }]);
        setMessage("");
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                <div className="chat-header">💬 Мессенджер</div>
                <div className="status">
                    {connected ? "🟢 Подключено" : "🔴 Подключение..."}
                </div>

                {!fixedRecipient ? (
                    <div className="recipient-setter">
                        <input
                            type="text"
                            value={recipientId}
                            onChange={(e) => setRecipientId(e.target.value)}
                            placeholder="Введите ID получателя"
                            className="recipient-input"
                        />
                        <button onClick={handleSetRecipient} className="set-button">
                            Начать чат
                        </button>
                    </div>
                ) : (
                    <div className="fixed-recipient">
                        📌 Чат с: <strong>{fixedRecipient}</strong>
                    </div>
                )}

                <div className="messages">
                    {messages.map((msg, idx) => (
                        <div key={idx} className="message">
                            <strong>{msg.sender}:</strong> {msg.text}
                        </div>
                    ))}
                </div>

                <form onSubmit={handleSend} className="input-form">
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Введите сообщение..."
                        className="message-input"
                        required
                    />
                    <button type="submit" className="send-button">
                        Отправить
                    </button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
