import React, { useEffect, useState } from 'react';
import { connect, sendMessage } from './socket';

function Chat({ currentUser, recipient }) {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');

    useEffect(() => {
        connect(currentUser.id, (msg) => {
            setMessages((prev) => [...prev, msg]);
        });

        fetch(`/messages/${currentUser.id}/${recipient.id}`)
            .then((res) => res.json())
            .then(setMessages);
    }, [currentUser.id, recipient.id]);

    const handleSend = () => {
        const message = {
            senderId: currentUser.id,
            recipientId: recipient.id,
            senderName: currentUser.name,
            content: input,
            timestamp: new Date(),
        };
        sendMessage(message);
        setMessages((prev) => [...prev, message]);
        setInput('');
    };

    return (
        <div>
            <h3>Chat with {recipient.name}</h3>
            <div style={{ border: '1px solid #ccc', padding: 10, height: 300, overflowY: 'scroll' }}>
                {messages.map((msg, index) => (
                    <div key={index}>
                        <b>{msg.senderName}:</b> {msg.content}
                    </div>
                ))}
            </div>
            <input
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Type a message"
            />
            <button onClick={handleSend}>Send</button>
        </div>
    );
}

export default Chat;
