import React, { useEffect, useState } from "react";

function Chat() {
    const [messages, setMessages] = useState([]);
    const [text, setText] = useState("");

    const fetchMessages = async () => {
        try {
            const res = await fetch("http://localhost:8080/chat/messages", {
                method: "GET",
                credentials: "include",
            });

            if (res.status === 403) {
                alert("❌ Доступ заборонено. Авторизуйтесь.");
                window.location.href = "/sign-in";
                return;
            }

            const data = await res.json();
            setMessages(data);
        } catch (err) {
            console.error("Fetch error", err);
        }
    };

    const sendMessage = async () => {
        try {
            await fetch("http://localhost:8080/chat/send", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ text }),
                credentials: "include",
            });

            setText("");
            fetchMessages();
        } catch (err) {
            console.error("Send error", err);
        }
    };

    useEffect(() => {
        fetchMessages();
    }, []);

    return (
        <div>
            <h1>Чат</h1>
            <ul>
                {messages.map((msg, idx) => (
                    <li key={idx}>{msg.text}</li>
                ))}
            </ul>
            <input value={text} onChange={(e) => setText(e.target.value)} placeholder="Повідомлення..." />
            <button onClick={sendMessage}>Відправити</button>
        </div>
    );
}

export default Chat;
