import React, { useEffect, useState, useRef } from "react";
import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs";

const Chat = ({ userId, recipientId, userName }) => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const stompClientRef = useRef(null);

    useEffect(() => {
        const socket = new SockJS("http://localhost:8080/ws");
        const stompClient = new Client({
            webSocketFactory: () => socket,
            onConnect: () => {
                stompClient.subscribe(`/user/${userId}/queue/messages`, (message) => {
                    const newMsg = JSON.parse(message.body);
                    fetchMessageById(newMsg.id);
                });
            },
        });

        stompClient.activate();
        stompClientRef.current = stompClient;

        fetchMessages();

        return () => stompClient.deactivate();
    }, [userId]);

    const fetchMessages = async () => {
        const res = await fetch(`/messages/${userId}/${recipientId}`);
        const data = await res.json();
        setMessages(data);
    };

    const fetchMessageById = async (id) => {
        const res = await fetch(`/messages/${id}`);
        const data = await res.json();
        setMessages((prev) => [...prev, data]);
    };

    const sendMessage = () => {
        const msg = {
            senderId: userId,
            recipientId: recipientId,
            senderName: userName,
            content: input,
        };

        stompClientRef.current.publish({
            destination: "/app/chat",
            body: JSON.stringify(msg),
        });

        setInput("");
    };

    return (
        <div className="p-4 border w-1/2 mx-auto mt-10 rounded shadow">
            <h2 className="text-xl mb-4">Чат с пользователем {recipientId}</h2>
            <div className="h-60 overflow-y-scroll border p-2 mb-2">
                {messages.map((msg, index) => (
                    <div key={index} className={msg.senderId === userId ? "text-right" : "text-left"}>
                        <p><strong>{msg.senderName}</strong>: {msg.content}</p>
                    </div>
                ))}
            </div>
            <div className="flex">
                <input
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    className="flex-grow p-2 border rounded-l"
                    placeholder="Введите сообщение..."
                />
                <button onClick={sendMessage} className="bg-blue-500 text-white px-4 rounded-r">
                    Отправить
                </button>
            </div>
        </div>
    );
};

export default Chat;
