import React, { useState } from "react";
import Chat from "./Chat";

function App() {
    const [currentUser, setCurrentUser] = useState("1");
    const recipientId = currentUser === "1" ? "2" : "1";

    return (
        <div>
            <div style={{ marginBottom: 10 }}>
                <button onClick={() => setCurrentUser("1")}>Я — Пользователь 1</button>
                <button onClick={() => setCurrentUser("2")}>Я — Пользователь 2</button>
            </div>
            <Chat userId={currentUser} recipientId={recipientId} />
        </div>
    );
}

export default App;
