import React, { useState, useEffect, useRef } from "react";
import { Client } from '@stomp/stompjs';
import SockJS from 'sockjs-client';
import './Chat.css';

function Chat() {
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);
    const [recipientUsername, setRecipientUsername] = useState("");
    const [myUsername, setMyUsername] = useState(null);
    const [connected, setConnected] = useState(false);
    const clientRef = useRef(null);

    const getToken = () => {
        const match = document.cookie.match(new RegExp('(^| )token=([^;]+)'));
        return match ? match[2] : null;
    };

    const fetchMyUsername = async () => {
        try {
            const res = await fetch("/auth/me", {
                headers: {
                    Authorization: `Bearer ${getToken()}`,
                }
            });
            if (res.ok) {
                const data = await res.json();
                setMyUsername(data.username); // ‚Üê –ø—Ä–µ–¥–ø–æ–ª–∞–≥–∞–µ—Ç—Å—è, —á—Ç–æ API –≤–æ–∑–≤—Ä–∞—â–∞–µ—Ç { username: "Yaroslav" }
            } else {
                throw new Error("–ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å –∏–º—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è");
            }
        } catch (err) {
            console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ —Ç–µ–∫—É—â–µ–≥–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:", err);
            alert("–û—à–∏–±–∫–∞ –∞—É—Ç–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ü–∏–∏. –í–æ–π–¥–∏—Ç–µ —Å–Ω–æ–≤–∞.");
            window.location.href = "/";
        }
    };

    const fetchMessages = async (recipient) => {
        try {
            const res = await fetch(`/messages/${myUsername}/${recipient}`);
            if (res.ok) {
                const data = await res.json();
                setMessages(data.map(msg => ({
                    sender: msg.senderId === myUsername ? "–Ø" : "–û–Ω",
                    text: msg.content
                })));
            }
        } catch (err) {
            console.error("–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å —Å–æ–æ–±—â–µ–Ω–∏—è", err);
        }
    };

    useEffect(() => {
        const token = getToken();
        if (!token) {
            alert("‚ùå –ù–µ—Ç —Ç–æ–∫–µ–Ω–∞. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–æ–π–¥–∏—Ç–µ —Å–Ω–æ–≤–∞.");
            window.location.href = "/";
            return;
        }

        fetchMyUsername().then(() => {
            const socket = new SockJS("/ws");
            const client = new Client({
                webSocketFactory: () => socket,
                connectHeaders: { Authorization: `Bearer ${token}` },
                onConnect: () => {
                    console.log("üü¢ –ü–æ–¥–∫–ª—é—á–µ–Ω–æ –∫ WebSocket");
                    setConnected(true);

                    client.subscribe("/user/queue/messages", (msg) => {
                        const body = JSON.parse(msg.body);
                        if (
                            (body.senderId === recipientUsername && body.recipientId === myUsername) ||
                            (body.senderId === myUsername && body.recipientId === recipientUsername)
                        ) {
                            setMessages(prev => [...prev, {
                                sender: body.senderId === myUsername ? "–Ø" : "–û–Ω",
                                text: body.content
                            }]);
                        }
                    });
                },
                onStompError: (frame) => {
                    console.error("STOMP –æ—à–∏–±–∫–∞: ", frame);
                },
                reconnectDelay: 5000,
            });

            client.activate();
            clientRef.current = client;

            return () => {
                client.deactivate();
                setConnected(false);
            };
        });
    }, []);

    const handleSend = (e) => {
        e.preventDefault();
        const client = clientRef.current;

        if (!message.trim() || !recipientUsername.trim()) return;

        if (!client || !client.connected) {
            alert("‚õî –ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ –µ—â—ë –Ω–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ.");
            return;
        }

        const payload = {
            senderId: myUsername,
            recipientId: recipientUsername,
            content: message,
        };

        client.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages([...messages, { sender: "–Ø", text: message }]);
        setMessage("");
    };

    const handleRecipientChange = (e) => {
        const value = e.target.value;
        setRecipientUsername(value);
        if (value && myUsername) {
            fetchMessages(value);
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                <div className="chat-header">üí¨ –ú–µ—Å—Å–µ–Ω–¥–∂–µ—Ä</div>
                <div className="status">{connected ? "üü¢ –ü–æ–¥–∫–ª—é—á–µ–Ω–æ" : "üî¥ –ü–æ–¥–∫–ª—é—á–µ–Ω–∏–µ..."}</div>

                <input
                    type="text"
                    value={recipientUsername}
                    onChange={handleRecipientChange}
                    placeholder="–ò–º—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –ø–æ–ª—É—á–∞—Ç–µ–ª—è"
                    className="recipient-input"
                />

                <div className="messages">
                    {messages.map((msg, idx) => (
                        <div key={idx} className="message">
                            <strong>{msg.sender}:</strong> {msg.text}
                        </div>
                    ))}
                </div>

                <form onSubmit={handleSend} className="input-form">
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ..."
                        className="message-input"
                        required
                    />
                    <button type="submit" className="send-button">–û—Ç–ø—Ä–∞–≤–∏—Ç—å</button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
