// src/Chat.js
import React, { useState, useEffect, useRef } from "react";
import { Client } from '@stomp/stompjs';
import SockJS from 'sockjs-client';
import { jwtDecode } from 'jwt-decode';
import './Chat.css';

function Chat() {
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);
    const [recipientId, setRecipientId] = useState("");
    const [myId, setMyId] = useState(null);
    const [users, setUsers] = useState([]);
    const clientRef = useRef(null);

    const getToken = () => {
        const match = document.cookie.match(new RegExp('(^| )token=([^;]+)'));
        return match ? match[2] : null;
    };

    const fetchUsers = async (myId) => {
        // –≠–º—É–ª–∏—Ä—É–µ–º —Å–ø–∏—Å–æ–∫ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π –∏–∑ —Ç–≤–æ–µ–π –±–∞–∑—ã (–º–æ–∂–Ω–æ –∑–∞–º–µ–Ω–∏—Ç—å –Ω–∞ fetch –∏–∑ backend)
        const fakeUsers = [
            { id: "1", name: "Yaroslav" },
            { id: "2", name: "Yarik228" },
        ];
        setUsers(fakeUsers.filter(user => user.id !== myId));
    };

    const fetchMessages = async (recipientId) => {
        try {
            const res = await fetch(`/messages/${myId}/${recipientId}`);
            if (res.ok) {
                const data = await res.json();
                setMessages(
                    data.map(msg => ({
                        sender: msg.senderId === myId ? "–Ø" : "–û–Ω",
                        text: msg.content
                    }))
                );
            }
        } catch (err) {
            console.error("–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å —Å–æ–æ–±—â–µ–Ω–∏—è", err);
        }
    };

    useEffect(() => {
        const token = getToken();
        if (!token) {
            alert("‚ùå –ù–µ—Ç —Ç–æ–∫–µ–Ω–∞. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–æ–π–¥–∏—Ç–µ —Å–Ω–æ–≤–∞.");
            window.location.href = "/";
            return;
        }

        const decoded = jwtDecode(token);
        const userId = decoded.sub || decoded.userId;
        setMyId(userId);
        fetchUsers(userId);

        const socket = new SockJS("/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            connectHeaders: { Authorization: `Bearer ${token}` },
            onConnect: () => {
                console.log("üü¢ –ü–æ–¥–∫–ª—é—á–µ–Ω–æ –∫ WebSocket");
                client.subscribe("/user/queue/messages", (msg) => {
                    const body = JSON.parse(msg.body);
                    if (
                        (body.senderId == recipientId && body.recipientId == myId) ||
                        (body.senderId == myId && body.recipientId == recipientId)
                    ) {
                        setMessages(prev => [...prev, {
                            sender: body.senderId === myId ? "–Ø" : "–û–Ω",
                            text: body.content
                        }]);
                    }
                });
            },
            debug: (str) => console.log(str),
            reconnectDelay: 5000,
        });

        client.activate();
        clientRef.current = client;

        return () => client.deactivate();
    }, []);

    const handleSend = (e) => {
        e.preventDefault();
        if (!message.trim() || !recipientId.trim()) return;

        const payload = {
            senderId: myId,
            recipientId,
            content: message,
        };

        clientRef.current.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages([...messages, { sender: "–Ø", text: message }]);
        setMessage("");
    };

    const handleRecipientChange = (e) => {
        setRecipientId(e.target.value);
        if (e.target.value && myId) {
            fetchMessages(e.target.value);
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                <div className="chat-header">üí¨ –ú–µ—Å—Å–µ–Ω–¥–∂–µ—Ä</div>

                <select
                    value={recipientId}
                    onChange={handleRecipientChange}
                    className="recipient-input"
                >
                    <option value="">–í—ã–±–µ—Ä–∏—Ç–µ —Å–æ–±–µ—Å–µ–¥–Ω–∏–∫–∞</option>
                    {users.map(user => (
                        <option key={user.id} value={user.id}>{user.name}</option>
                    ))}
                </select>

                <div className="messages">
                    {messages.map((msg, idx) => (
                        <div key={idx} className="message">
                            <strong>{msg.sender}:</strong> {msg.text}
                        </div>
                    ))}
                </div>

                <form onSubmit={handleSend} className="input-form">
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ..."
                        className="message-input"
                        required
                    />
                    <button type="submit" className="send-button">–û—Ç–ø—Ä–∞–≤–∏—Ç—å</button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
