import React, { useState, useEffect, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import { jwtDecode } from "jwt-decode";
import "./Chat.css";

function Chat() {
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);
    const [recipientId, setRecipientId] = useState("");
    const [myId, setMyId] = useState(null);
    const [myName, setMyName] = useState("");
    const [connected, setConnected] = useState(false);
    const [allUsers, setAllUsers] = useState([]);

    const clientRef = useRef(null);
    const recipientIdRef = useRef("");

    const getToken = () => {
        const match = document.cookie.match(new RegExp("(^| )token=([^;]+)"));
        return match ? match[2] : null;
    };

    const fetchMessages = async (recipientId) => {
        try {
            const token = getToken();
            const res = await fetch(`/messages/${myId}/${recipientId}`, {
                headers: { Authorization: `Bearer ${token}` },
            });
            if (res.ok) {
                const data = await res.json();
                setMessages(data.map((msg) => ({
                    sender: msg.senderId === myId ? "Я" : "Он",
                    text: msg.content,
                    timestamp: msg.timestamp,
                    senderName: msg.senderName,
                })));
            } else {
                console.error("Ошибка загрузки сообщений:", res.status);
            }
        } catch (err) {
            console.error("Ошибка при загрузке сообщений:", err);
        }
    };

    useEffect(() => {
        if (recipientId && myId) {
            fetchMessages(recipientId);
        }
    }, [recipientId, myId]);

    // 👇 Добавляем интервал для постоянного обновления сообщений
    useEffect(() => {
        if (!recipientId || !myId) return;

        const interval = setInterval(() => {
            fetchMessages(recipientId);
        }, 5000); // обновление каждые 5 секунд

        return () => clearInterval(interval);
    }, [recipientId, myId]);

    useEffect(() => {
        const token = getToken();
        if (!token) {
            alert("Нет токена. Пожалуйста, войдите снова.");
            window.location.href = "/";
            return;
        }

        const decoded = jwtDecode(token);
        const id = decoded.userId;
        if (!id) {
            alert("Токен не содержит userId.");
            return;
        }
        setMyId(id);
        setMyName(decoded.username);

        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            connectHeaders: { Authorization: `Bearer ${token}` },
            reconnectDelay: 5000,
            onConnect: () => {
                setConnected(true);
                client.subscribe(`/user/${id}/queue/messages`, (msg) => {
                    const body = JSON.parse(msg.body);
                    const otherUserId = body.senderId;

                    if (recipientIdRef.current === otherUserId) {
                        setMessages(prevMessages => [
                            ...prevMessages,
                            {
                                sender: body.senderId === myId ? "Я" : "Он",
                                text: body.content,
                                timestamp: body.timestamp,
                                senderName: body.senderName,
                            }
                        ]);
                    } else {
                        console.log(`📩 Новое сообщение от ${body.senderName}`);
                    }
                });
            },
            onStompError: (frame) => {
                console.error("STOMP ошибка:", frame);
            },
        });

        client.activate();
        clientRef.current = client;

        return () => {
            client.deactivate();
            setConnected(false);
        };
    }, []);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const token = getToken();
                const res = await fetch("/users/all", {
                    headers: { Authorization: `Bearer ${token}` },
                });

                if (res.ok) {
                    const data = await res.json();
                    setAllUsers(data.filter((user) => user.id !== myId));
                } else {
                    console.error("Ошибка при получении пользователей:", res.status);
                }
            } catch (err) {
                console.error("Ошибка при загрузке пользователей:", err);
            }
        };

        if (myId) {
            fetchUsers();
        }
    }, [myId]);

    const handleSend = (e) => {
        e.preventDefault();
        const client = clientRef.current;
        if (!message.trim() || !recipientId.trim()) return;

        if (!client || !client.connected) {
            alert("Подключение ещё не установлено.");
            return;
        }

        const recipientUser = allUsers.find((u) => u.id === recipientId);
        const recipientName = recipientUser ? recipientUser.username : "Получатель";

        const payload = {
            senderId: myId,
            recipientId,
            content: message,
            senderName: myName,
            recipientName: recipientName,
            timestamp: new Date().toISOString(),
        };

        client.publish({
            destination: "/app/chat",
            body: JSON.stringify(payload),
        });

        setMessages((prev) => [
            ...prev,
            {
                sender: "Я",
                text: message,
                timestamp: new Date().toLocaleString(),
                senderName: myName,
            },
        ]);
        setMessage("");
    };

    const handleRecipientChange = (e) => {
        const value = e.target.value;
        recipientIdRef.current = value;
        setRecipientId(value);
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                <div className="chat-header">💬 Мессенджер</div>
                <div className="status">
                    {connected ? "🟢 Подключено" : "🔴 Подключение..."}
                </div>
                <select
                    value={recipientId}
                    onChange={handleRecipientChange}
                    className="recipient-input"
                >
                    <option value="">-- Выберите получателя --</option>
                    {allUsers.map((user) => (
                        <option key={user.id} value={user.id}>
                            {user.username} ({user.email})
                        </option>
                    ))}
                </select>
                <div className="messages">
                    {messages.map((msg, idx) => (
                        <div key={idx} className="message">
                            <strong>{msg.senderName}:</strong> {msg.text}{" "}
                            <span>{msg.timestamp}</span>
                        </div>
                    ))}
                </div>
                <form onSubmit={handleSend} className="input-form">
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Введите сообщение..."
                        className="message-input"
                        required
                    />
                    <button type="submit" className="send-button">
                        Отправить
                    </button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
