import React, { useEffect, useState, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";
import "./Chat.css";

function Chat({ userId, recipientId }) {
    const [message, setMessage] = useState("");
    const [messages, setMessages] = useState([]);
    const stompClientRef = useRef(null);

    useEffect(() => {
        const socket = new SockJS(`http://localhost:8080/ws?userId=${userId}`);
        const stompClient = new Client({
            webSocketFactory: () => socket,
            reconnectDelay: 5000,
            onConnect: () => {
                console.log("âœ… Connected as userId =", userId);

                stompClient.subscribe("/user/queue/messages", (msg) => {
                    const notification = JSON.parse(msg.body);
                    fetch(`http://localhost:8080/messages/${notification.id}`)
                        .then((res) => res.json())
                        .then((data) => {
                            setMessages((prev) => [
                                ...prev,
                                { sender: data.senderName, text: data.content },
                            ]);
                        });
                });
            },
            onStompError: (frame) => {
                console.error("âŒ STOMP error", frame);
            },
        });

        stompClient.activate();
        stompClientRef.current = stompClient;

        return () => {
            stompClient.deactivate();
        };
    }, [userId]);

    const handleSend = (e) => {
        e.preventDefault();
        if (!message.trim()) return;

        const chatMessage = {
            senderId: userId,
            recipientId: recipientId,
            senderName: userId,
            recipientName: recipientId,
            content: message,
            timestamp: new Date(),
        };

        stompClientRef.current.publish({
            destination: "/app/chat",
            body: JSON.stringify(chatMessage),
        });

        setMessages((prev) => [...prev, { sender: "Ð¯", text: message }]);
        setMessage("");
    };

    return (
        <div className="chat-container">
            <div className="chat-box">
                <div className="chat-header">ðŸ’¬ ÐŸÐ¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»ÑŒ {userId}</div>
                <div className="messages">
                    {messages.map((msg, idx) => (
                        <div key={idx} className="message">
                            <strong>{msg.sender}:</strong> {msg.text}
                        </div>
                    ))}
                </div>
                <form onSubmit={handleSend} className="input-form">
                    <input
                        type="text"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        placeholder="Ð’Ð²ÐµÐ´Ð¸Ñ‚Ðµ ÑÐ¾Ð¾Ð±Ñ‰ÐµÐ½Ð¸Ðµ..."
                        className="message-input"
                        required
                    />
                    <button type="submit" className="send-button">ÐžÑ‚Ð¿Ñ€Ð°Ð²Ð¸Ñ‚ÑŒ</button>
                </form>
            </div>
        </div>
    );
}

export default Chat;
