import React, { useState } from "react";

function SignIn() {
    const [form, setForm] = useState({ email: "", password: "" });

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await fetch("/auth/sign-in", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(form),
            });

            const data = await res.json();

            if (res.ok) {
                document.cookie = `token=${data.token}; path=/; max-age=${7 * 24 * 60 * 60}; Secure; SameSite=Strict`;
                alert("✅ Успішний вхід!");
                window.location.href = "/chat";
            } else {
                alert("❌ Помилка: " + (data.message || "Невідома помилка"));
            }
        } catch (err) {
            alert("❌ Сервер не відповідає або сталася помилка: " + err.message);
        }
    };

    return (
        <div style={styles.container}>
            <form onSubmit={handleSubmit} style={styles.form}>
                <h2 style={styles.title}>Вхід</h2>
                <input
                    style={styles.input}
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={form.email}
                    onChange={handleChange}
                    required
                />
                <input
                    style={styles.input}
                    type="password"
                    name="password"
                    placeholder="Пароль"
                    value={form.password}
                    onChange={handleChange}
                    required
                />
                <button type="submit" style={styles.button}>
                    Увійти
                </button>
            </form>
        </div>
    );
}

const styles = { /* тот же как у тебя */ };

export default SignIn;
